                            className={`${log.includes('OPTIMAL') || log.includes('secured') ? 'text-emerald-400' : log.includes('ACTIVE') || log.includes('initialized') || log.includes('[INPUT]') ? 'text-intel-gold' : 'text-zinc-500'}`}
