                            {/* Scanning Light Logic */}
                            {isScanning && (
                                <motion.div
                                    className="absolute left-0 right-0 h-1 bg-noble-gold shadow-[0_0_20px_#d4af37] z-20"
                                    animate={{ top: ["0%", "100%", "0%"] }}
                                    transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                                />
