'use client';
import { useState, useEffect, useCallback } from 'react';
import {
    Brain,
    Zap,
    Target,
    Clock,
    ShieldCheck,
    Heart,
    RefreshCw,
    Eye,
    Cpu,
    ZapOff
} from 'lucide-react';
import { useIntelligence } from '@/context/IntelligenceContext';

type GameMode = 'memory' | 'inhibition' | 'logic' | 'speed';

export default function LeadershipGym({ onXPAction }: { onXPAction?: (amount: number) => void }) {
    const [mode, setMode] = useState<GameMode>('memory');
    const [gameActive, setGameActive] = useState(false);
    const [score, setScore] = useState(0);
    const [level, setLevel] = useState(1);
    const [timeLeft, setTimeLeft] = useState(30);

    // Memory Game State
    const [sequence, setSequence] = useState<number[]>([]);
    const [userInput, setUserInput] = useState<number[]>([]);
    const [isFlashing, setIsFlashing] = useState(false);
    const [activeColor, setActiveColor] = useState<number | null>(null);

    // Inhibition Game State
    const [stroopWord, setStroopWord] = useState('');
    const [stroopColor, setStroopColor] = useState('');

    // Speed Game State
    const [mathProblem, setMathProblem] = useState({ q: '', a: 0 });
    const [mathOptions, setMathOptions] = useState<number[]>([]);

    // Logic Game State
    const [pattern, setPattern] = useState<number[]>([]);
    const [patternMissing, setPatternMissing] = useState(0);
    const [patternOptions, setPatternOptions] = useState<number[]>([]);

    const colors = [
        { id: 0, name: 'Red', bg: 'bg-red-500', text: 'text-red-500' },
        { id: 1, name: 'Blue', bg: 'bg-blue-500', text: 'text-blue-500' },
        { id: 2, name: 'Green', bg: 'bg-green-500', text: 'text-green-500' },
        { id: 3, name: 'Yellow', bg: 'bg-yellow-500', text: 'text-yellow-500' }
    ];

    const startNewRound = useCallback(() => {
        if (mode === 'memory') {
            const newLength = 2 + level;
            const newSeq = Array.from({ length: newLength }, () => Math.floor(Math.random() * 4));
            setSequence(newSeq);
            setUserInput([]);
            flashSequence(newSeq);
        } else if (mode === 'inhibition') {
            const wordIdx = Math.floor(Math.random() * 4);
            const colorIdx = Math.floor(Math.random() * 4);
            setStroopWord(colors[wordIdx].name);
            setStroopColor(colors[colorIdx].bg);
        } else if (mode === 'speed') {
            const a = Math.floor(Math.random() * (level * 5)) + 1;
            const b = Math.floor(Math.random() * (level * 5)) + 1;
            const ans = a + b;
            setMathProblem({ q: `${a} + ${b}`, a: ans });
            const options = [ans, ans + 1, ans - 1, ans + 2].sort(() => Math.random() - 0.5);
            setMathOptions(options);
        } else if (mode === 'logic') {
            const start = Math.floor(Math.random() * 10);
            const step = Math.floor(Math.random() * 5) + 1;
            const fullPattern = [start, start + step, start + (step * 2), start + (step * 3)];
            const missingIdx = Math.floor(Math.random() * 4);
            const missingVal = fullPattern[missingIdx];

            setPattern(fullPattern);
            setPatternMissing(missingIdx);

            const options = [missingVal, missingVal + 1, missingVal - 1, missingVal + step].sort(() => Math.random() - 0.5);
            setPatternOptions(options);
        }
    }, [mode, level]);

    const flashSequence = async (seq: number[]) => {
        setIsFlashing(true);
        for (const colorId of seq) {
            setActiveColor(colorId);
            await new Promise(r => setTimeout(r, 600));
            setActiveColor(null);
            await new Promise(r => setTimeout(r, 200));
        }
        setIsFlashing(false);
    };

    const startGame = () => {
        setGameActive(true);
        setScore(0);
        setLevel(1);
        setTimeLeft(30);
        startNewRound();
    };

    const handleInput = (val: any) => {
        if (!gameActive || isFlashing) return;

        if (mode === 'memory') {
            const colorId = val as number;
            const newInput = [...userInput, colorId];
            setUserInput(newInput);
            if (colorId !== sequence[newInput.length - 1]) {
                setGameActive(false);
            } else if (newInput.length === sequence.length) {
                setScore(score + 10);
                setLevel(level + 1);
                setTimeout(startNewRound, 500);
            }
        } else if (mode === 'inhibition') {
            const selectedColorName = val as string;
            // The goal is to click the word, or the color? Let's say click the COLOR of the text.
            const colorName = colors.find(c => c.bg === stroopColor)?.name;
            if (selectedColorName === colorName) {
                setScore(score + 5);
                startNewRound();
            } else {
                setScore(Math.max(0, score - 2));
                startNewRound();
            }
        } else if (mode === 'speed') {
            if (val === mathProblem.a) {
                setScore(score + 5);
                if (score > 0 && score % 20 === 0) setLevel(level + 1);
                startNewRound();
            } else {
                setScore(Math.max(0, score - 2));
                startNewRound();
            }
        } else if (mode === 'logic') {
            if (val === pattern[patternMissing]) {
                setScore(score + 15);
                startNewRound();
                if (onXPAction) onXPAction(2);
            } else {
                setScore(Math.max(0, score - 5));
                startNewRound();
            }
        }
    };

    const { triggerBriefing } = useIntelligence();

    useEffect(() => {
        if (gameActive && timeLeft > 0) {
            const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
            return () => clearTimeout(timer);
        } else if (timeLeft === 0) {
            setGameActive(false);
        }
    }, [gameActive, timeLeft]);

    return (
        <div className="p-10 rounded-[2.5rem] bg-gradient-to-br from-zinc-950 via-cyan-950 to-zinc-950 text-white shadow-3xl border border-cyan-900/50 relative overflow-hidden group">
            {/* Background Effects */}
            <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(6,182,212,0.1),transparent)] pointer-events-none" />

            <div className="relative z-10">
                <div className="flex items-center justify-between mb-10">
                    <div className="flex items-center gap-5">
                        <div className="w-16 h-16 rounded-3xl bg-cyan-600/20 border border-cyan-500/30 flex items-center justify-center shadow-xl shadow-cyan-900/20">
                            <Brain className="text-cyan-400" size={32} />
                        </div>
                        <div>
                            <h2 className="text-3xl font-black tracking-tighter">Leadership Workout</h2>
                            <p className="text-sm font-bold text-cyan-400 uppercase tracking-widest">Mental Performance Exercises</p>
                        </div>
                    </div>
                    <div className="flex items-center gap-4">
                        <button
                            onClick={() => triggerBriefing('Legacy Profile')}
                            className="flex items-center gap-2 px-4 py-2 rounded-xl bg-amber-500/10 border border-amber-500/20 text-amber-400 hover:bg-amber-500/20 transition-all group"
                        >
                            <Target size={14} className="group-hover:rotate-45 transition-transform" />
                            <span className="text-[10px] font-black uppercase tracking-widest">Founder Hub</span>
                        </button>
                        <div className="flex gap-4">
                            <div className="px-5 py-3 rounded-2xl bg-zinc-900 border border-zinc-800 text-center min-w-[80px]">
                                <div className="text-2xl font-black text-yellow-400 leading-none mb-1">{score}</div>
                                <div className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest">Score</div>
                            </div>
                            <div className="px-5 py-3 rounded-2xl bg-zinc-900 border border-zinc-800 text-center min-w-[80px]">
                                <div className="text-2xl font-black text-purple-400 leading-none mb-1">{level}</div>
                                <div className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest">Stage</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Mode Selector */}
                <div className="grid grid-cols-4 gap-3 mb-10">
                    {[
                        { id: 'memory', label: 'Retention', icon: <Cpu size={16} /> },
                        { id: 'inhibition', label: 'Focus Check', icon: <ZapOff size={16} /> },
                        { id: 'speed', label: 'Quick Math', icon: <Zap size={16} /> },
                        { id: 'logic', label: 'Pattern Finder', icon: <Eye size={16} /> }
                    ].map((m) => (
                        <button
                            key={m.id}
                            onClick={() => { setMode(m.id as GameMode); setGameActive(false); }}
                            className={`flex flex-col items-center gap-2 p-4 rounded-2xl border transition-all ${mode === m.id
                                ? 'bg-cyan-500/20 border-cyan-500 text-cyan-300 shadow-lg shadow-cyan-900/20'
                                : 'bg-zinc-900/50 border-zinc-800 text-zinc-500 hover:border-zinc-700'
                                }`}
                        >
                            {m.icon}
                            <span className="text-[10px] font-bold uppercase tracking-wider text-center">{m.label}</span>
                        </button>
                    ))}
                </div>

                {/* Game Area */}
                <div className="min-h-[300px] flex flex-col items-center justify-center p-8 rounded-[2rem] bg-black/40 border border-zinc-900 relative">
                    {!gameActive ? (
                        <div className="text-center space-y-6">
                            <h3 className="text-xl font-bold text-zinc-300">
                                {mode === 'memory' && "Sequence Mastery"}
                                {mode === 'inhibition' && "Color Challenge"}
                                {mode === 'speed' && "Math Sprints"}
                                {mode === 'logic' && "Pattern Match"}
                            </h3>
                            <p className="text-sm text-zinc-500 max-w-sm mx-auto">
                                {mode === 'memory' && "Improve your working memory by recalling increasing sequences."}
                                {mode === 'inhibition' && "Click the NAME of the color, ignoring the text color. Improves decision making."}
                                {mode === 'speed' && "Solve rapid math equations to increase processing speed."}
                                {mode === 'logic' && "Identify the missing piece of the pattern. Improves abstract reasoning."}
                            </p>
                            <button
                                onClick={startGame}
                                className="px-10 py-5 bg-gradient-to-r from-cyan-600 to-blue-600 rounded-2xl font-black text-sm uppercase tracking-[0.2em] shadow-2xl shadow-cyan-900/40 hover:scale-105 transition-all flex items-center gap-3 mx-auto relative overflow-hidden group"
                            >
                                <span className="relative z-10 flex items-center gap-3">
                                    <RefreshCw size={18} />
                                    Start Workout
                                </span>
                                <div className="absolute inset-0 bg-white/20 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700" />
                            </button>
                        </div>
                    ) : (
                        <div className="w-full h-full flex flex-col items-center justify-center">
                            <div className="absolute top-6 right-8 flex items-center gap-2 text-orange-500">
                                <Clock size={18} />
                                <span className="text-2xl font-black font-mono">{timeLeft}s</span>
                            </div>

                            {mode === 'memory' && (
                                <div className="grid grid-cols-2 gap-6 w-full max-w-xs">
                                    {colors.map((c) => (
                                        <button
                                            key={c.id}
                                            onClick={() => handleInput(c.id)}
                                            className={`h-24 rounded-3xl transition-all transform active:scale-95 border-4 ${activeColor === c.id ? `bg-white border-white scale-110` : `${c.bg} border-transparent opacity-60`
                                                }`}
                                        />
                                    ))}
                                </div>
                            )}

                            {mode === 'inhibition' && (
                                <div className="text-center space-y-12">
                                    <div className="text-7xl font-black uppercase tracking-tighter p-4 transition-all duration-300">
                                        <span className={stroopColor.replace('bg-', 'text-')}>{stroopWord}</span>
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {colors.map((c) => (
                                            <button
                                                key={c.id}
                                                onClick={() => handleInput(c.name)}
                                                className="px-8 py-5 rounded-2xl bg-zinc-900 border border-zinc-800 text-sm font-bold hover:bg-zinc-800 hover:border-cyan-500/50 transition-all uppercase tracking-[0.2em] shadow-lg"
                                            >
                                                {c.name}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {mode === 'speed' && (
                                <div className="text-center space-y-12">
                                    <div className="text-7xl font-black text-cyan-400 font-mono tracking-tighter">
                                        {mathProblem.q}
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {mathOptions.map((opt, i) => (
                                            <button
                                                key={i}
                                                onClick={() => handleInput(opt)}
                                                className="px-10 py-6 rounded-2xl bg-zinc-900 border border-zinc-800 text-2xl font-black hover:bg-zinc-800 transition-all text-zinc-300"
                                            >
                                                {opt}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {mode === 'logic' && (
                                <div className="text-center space-y-12">
                                    <div className="flex items-center gap-6 justify-center">
                                        {pattern.map((p, i) => (
                                            <div key={i} className={`w-20 h-20 rounded-2xl flex items-center justify-center text-4xl font-black ${i === patternMissing ? 'bg-zinc-800 border-2 border-dashed border-cyan-500/50 text-cyan-500 animate-pulse' : 'bg-zinc-900 border border-white/5 text-white'}`}>
                                                {i === patternMissing ? '?' : p}
                                            </div>
                                        ))}
                                    </div>
                                    <div className="grid grid-cols-2 gap-4 max-w-xs mx-auto">
                                        {patternOptions.map((opt, i) => (
                                            <button
                                                key={i}
                                                onClick={() => handleInput(opt)}
                                                className="px-8 py-5 rounded-2xl bg-zinc-900 border border-zinc-800 text-xl font-black hover:border-cyan-500 transition-all"
                                            >
                                                {opt}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                {/* Dashboard Stats */}
                <div className="mt-10 grid grid-cols-3 gap-6">
                    <StatItem label="Impulse Control" value="+42%" icon={<Target size={14} />} color="text-emerald-400" />
                    <StatItem label="Working Memory" value="+67%" icon={<Brain size={14} />} color="text-cyan-400" />
                    <StatItem label="Reaction Speed" value="-120ms" icon={<Zap size={14} />} color="text-orange-400" />
                </div>

                {/* Professional Centers Grid */}
                <div className="mt-12">
                    <p className="text-[10px] font-black text-zinc-600 uppercase tracking-[0.3em] mb-6 text-center">Stakeholder Cognitive Hubs</p>
                    <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                        <TrainingCenter
                            title="Student Focus"
                            description="Executive function & recall"
                            color="cyan"
                            icon={<Brain size={14} />}
                            onClick={() => triggerBriefing('Student Focus')}
                        />
                        <TrainingCenter
                            title="Teacher Guard"
                            description="Burnout shield & focus"
                            color="blue"
                            icon={<ShieldCheck size={14} />}
                            onClick={() => triggerBriefing('Teacher Guard')}
                        />
                        <TrainingCenter
                            title="Admin Vision"
                            description="Strategic decision velocity"
                            color="purple"
                            icon={<Target size={14} />}
                            onClick={() => triggerBriefing('Admin Vision')}
                        />
                        <TrainingCenter
                            title="Parent Sync"
                            description="Cognitive patience & calm"
                            color="orange"
                            icon={<Heart size={14} />}
                            onClick={() => triggerBriefing('Parent Sync')}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

function StatItem({ label, value, icon, color }: { label: string, value: string, icon: React.ReactNode, color: string }) {
    return (
        <div className="p-5 rounded-2xl bg-zinc-950 border border-zinc-900 flex flex-col items-center">
            <div className={`mb-2 ${color} opacity-80`}>{icon}</div>
            <div className={`text-2xl font-black ${color}`}>{value}</div>
            <div className="text-[9px] font-bold text-zinc-600 uppercase tracking-widest">{label}</div>
        </div>
    );
}

function TrainingCenter({ title, description, color, icon, onClick }: { title: string, description: string, color: string, icon: React.ReactNode, onClick?: () => void }) {
    const colorClasses: Record<string, string> = {
        cyan: 'border-cyan-500/20 hover:border-cyan-500/50 bg-cyan-500/5 hover:shadow-[0_0_20px_rgba(34,211,238,0.1)]',
        blue: 'border-blue-500/20 hover:border-blue-500/50 bg-blue-500/5 hover:shadow-[0_0_20px_rgba(59,130,246,0.1)]',
        purple: 'border-purple-500/20 hover:border-purple-500/50 bg-purple-500/5 hover:shadow-[0_0_20px_rgba(168,85,247,0.1)]',
        orange: 'border-orange-500/20 hover:border-orange-500/50 bg-orange-500/5 hover:shadow-[0_0_20px_rgba(249,115,22,0.1)]',
    };

    return (
        <button
            onClick={onClick}
            className={`p-5 rounded-2xl border backdrop-blur-sm ${colorClasses[color]} transition-all duration-300 text-left group overflow-hidden relative hover:scale-[1.02]`}
        >
            <div className="relative z-10">
                <div className="flex items-center gap-2 mb-2">
                    <div className="opacity-60 group-hover:opacity-100 transition-opacity duration-300">{icon}</div>
                    <span className="text-[10px] font-black uppercase tracking-widest leading-none text-white/90">{title}</span>
                </div>
                <p className="text-[9px] text-zinc-500 leading-tight group-hover:text-zinc-300 transition-colors duration-300">{description}</p>
            </div>
            <div className="absolute inset-0 bg-gradient-to-br from-white/[0.02] to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500 rounded-2xl" />
        </button>
    );
}

'use client';
import { ExternalLink, Shield as LucideShield, BookOpen, GraduationCap, Building2, TrendingUp, Cpu, FlaskConical, Scale, Lightbulb, ChevronRight } from "lucide-react";
import { ALL_RESOURCES, RESOURCE_METADATA, get2025Updates, getLiteracyResources, getResearchValidation, getIDEACompliance } from '@/config/resources';

export default function ResourceVault() {
    const resources = ALL_RESOURCES;
    const updates2025 = get2025Updates();
    const literacyResources = getLiteracyResources();
    const researchValidation = getResearchValidation();
    const ideaCompliance = getIDEACompliance();

    const getCategoryIcon = (category: string) => {
        switch (category) {
            case 'state': return <LucideShield className="text-red-500" size={20} />;
            case 'district': return <Building2 className="text-blue-500" size={20} />;
            case 'instructional': return <BookOpen className="text-green-500" size={20} />;
            case 'professional': return <GraduationCap className="text-purple-500" size={20} />;
            case 'accountability': return <TrendingUp className="text-orange-500" size={20} />;
            case 'stem-career': return <Cpu className="text-cyan-500" size={20} />;
            case 'research': return <FlaskConical className="text-pink-500" size={20} />;
            case 'legal': return <Scale className="text-amber-500" size={20} />;
            case 'specialized': return <Lightbulb className="text-indigo-500" size={20} />;
            default: return <LucideShield size={20} />;
        }
    };

    const getPriorityBadge = (priority: string) => {
        switch (priority) {
            case 'critical': return 'bg-red-100 text-red-700 border-red-300 dark:bg-red-900/30 dark:text-red-400';
            case 'high': return 'bg-orange-100 text-orange-700 border-orange-300 dark:bg-orange-900/30 dark:text-orange-400';
            default: return 'bg-blue-100 text-blue-700 border-blue-300 dark:bg-blue-900/30 dark:text-blue-400';
        }
    };

    return (
        <div className="p-8 rounded-3xl bg-gradient-to-br from-slate-900 to-zinc-950 text-white shadow-2xl border border-slate-800">
            <div className="flex items-center justify-between mb-6">
                <div className="flex items-center gap-3">
                    <div className="w-12 h-12 rounded-full bg-slate-800 flex items-center justify-center relative overflow-hidden">
                        <div className="absolute inset-0 bg-red-500/10 animate-pulse" />
                        <LucideShield className="text-red-400 relative z-10" size={24} />
                    </div>
                    <div>
                        <h2 className="text-2xl font-bold">Professional Resource Vault</h2>
                        <p className="text-sm text-slate-300">ALSDE & MCPSS Compliance Hub</p>
                    </div>
                </div>
                <div className="flex items-center gap-2 bg-green-500/20 px-4 py-2 rounded-full border border-green-500/50">
                    <div className="w-2 h-2 bg-green-400 rounded-full animate-pulse" />
                    <span className="text-xs font-mono text-green-300">LIVE SYNC</span>
                </div>
            </div>

            {/* Smart Search */}
            <div className="mb-6 relative group">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                    <div className="h-2 w-2 rounded-full bg-red-500 animate-pulse" />
                </div>
                <input
                    type="text"
                    placeholder="Search protocols, acts, and compliance docs..."
                    className="w-full bg-slate-900/50 border border-slate-700 rounded-xl py-3 pl-10 pr-4 text-sm text-slate-300 focus:outline-none focus:border-red-500/50 focus:ring-1 focus:ring-red-500/50 transition-all placeholder:text-slate-600"
                />
                <div className="absolute right-3 top-3">
                    <span className="text-[10px] font-mono text-slate-600">CTRL+K</span>
                </div>
            </div>

            {/* Alabama Hub Gateway */}
            <a
                href="/resources/alabama"
                className="mb-4 p-5 rounded-2xl bg-gradient-to-r from-blue-600/20 to-emerald-600/20 border border-blue-500/30 flex items-center justify-between group hover:scale-[1.02] transition-all"
            >
                <div className="flex items-center gap-4">
                    <div className="w-10 h-10 rounded-xl bg-blue-600 flex items-center justify-center shadow-lg">
                        <Building2 className="text-white" size={20} />
                    </div>
                    <div>
                        <p className="text-xs font-black uppercase tracking-widest text-blue-400">Regional Gateway</p>
                        <p className="text-sm font-bold text-white group-hover:text-blue-300 transition-colors">Alabama Education Center</p>
                    </div>
                </div>
                <ChevronRight className="text-blue-500 group-hover:translate-x-1 transition-transform" size={20} />
            </a>

            {/* 2025 Updates Banner */}
            {updates2025.length > 0 && (
                <div className="mb-4 p-4 bg-yellow-900/30 border border-yellow-700 rounded-xl">
                    <div className="flex items-center gap-2 mb-2">
                        <TrendingUp className="text-yellow-400" size={16} />
                        <span className="text-sm font-bold text-yellow-400">2025 Updates Available</span>
                    </div>
                    <p className="text-xs text-yellow-200">
                        {updates2025.length} new resources including Digital Literacy Standards and Teacher Shortage Tracking
                    </p>
                </div>
            )}

            {/* Quick Stats */}
            <div className="grid grid-cols-4 gap-2 mb-6">
                <div className="p-3 rounded-lg bg-red-900/20 border border-red-800/50 text-center">
                    <p className="text-xs font-bold uppercase tracking-wider text-red-400">State/Legal</p>
                    <p className="text-lg font-black text-red-400">
                        {RESOURCE_METADATA.categories.state + RESOURCE_METADATA.categories.legal}
                    </p>
                </div>
                <div className="p-3 rounded-lg bg-green-900/20 border border-green-800/50 text-center">
                    <p className="text-xs font-bold uppercase tracking-wider text-green-400">Literacy</p>
                    <p className="text-lg font-black text-green-400">{literacyResources.length}</p>
                </div>
                <div className="p-3 rounded-lg bg-pink-900/20 border border-pink-800/50 text-center">
                    <p className="text-xs font-bold uppercase tracking-wider text-pink-400">Research</p>
                    <p className="text-lg font-black text-pink-400">{researchValidation.length}</p>
                </div>
                <div className="p-3 rounded-lg bg-indigo-900/20 border border-indigo-800/50 text-center">
                    <p className="text-xs font-bold uppercase tracking-wider text-indigo-400">Specialized</p>
                    <p className="text-lg font-black text-indigo-400">{RESOURCE_METADATA.categories.specialized}</p>
                </div>
            </div>

            {/* Resource Links */}
            <div className="space-y-3 max-h-96 overflow-y-auto pr-2">
                {resources.map((resource, index) => (
                    <a
                        key={index}
                        href={resource.url}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="block p-4 bg-slate-800/50 hover:bg-slate-800 rounded-xl border border-slate-700 hover:border-slate-600 transition-all group"
                    >
                        <div className="flex items-start justify-between mb-2">
                            <div className="flex items-center gap-2">
                                {getCategoryIcon(resource.category)}
                                <span className="font-semibold text-sm group-hover:text-blue-400 transition-colors">
                                    {resource.title}
                                </span>
                            </div>
                            <div className="flex items-center gap-2">
                                <span className={`text-[9px] uppercase font-bold px-2 py-0.5 rounded-full border ${getPriorityBadge(resource.priority)}`}>
                                    {resource.priority}
                                </span>
                                <ExternalLink size={14} className="text-slate-400 group-hover:text-blue-400 transition-colors" />
                            </div>
                        </div>
                        <p className="text-xs text-slate-400 leading-relaxed">{resource.description}</p>
                    </a>
                ))}
            </div>

            {/* Footer Stats */}
            <div className="mt-6 pt-6 border-t border-slate-800 grid grid-cols-3 gap-4 text-center">
                <div>
                    <p className="text-2xl font-black text-red-400">4</p>
                    <p className="text-xs text-slate-400">State Links</p>
                </div>
                <div>
                    <p className="text-2xl font-black text-blue-400">3</p>
                    <p className="text-xs text-slate-400">District Links</p>
                </div>
                <div>
                    <p className="text-2xl font-black text-green-400">100%</p>
                    <p className="text-xs text-slate-400">Compliance</p>
                </div>
            </div>
        </div>
    );
}

'use client';

import { useEffect, useState, useRef } from 'react';
import * as Y from 'yjs';
import { supabase } from '@/lib/supabase';
import { SupabaseYjsProvider } from '@/lib/collab/supabase-provider';
import { Card } from '@/components/ui/card';
import { Users, Zap, Shield, Activity } from 'lucide-react';
import { motion } from 'framer-motion';

export function CollaborativeWarRoom() {
    const [text, setText] = useState('');
    const [users, setUsers] = useState<number>(1);
    const ydocRef = useRef<Y.Doc>(new Y.Doc());
    const providerRef = useRef<SupabaseYjsProvider | null>(null);

    useEffect(() => {
        if (!supabase) return;

        console.log('[WarRoom] Initializing Liquid Data Link...');

        // Initialize Yjs Document
        const ydoc = ydocRef.current;
        const ytext = ydoc.getText('briefing');

        // Initialize Supabase Provider
        providerRef.current = new SupabaseYjsProvider(ydoc, supabase, 'war-room-alpha');

        // Sync local state with Yjs
        ytext.observe(() => {
            setText(ytext.toString());
        });

        return () => {
            providerRef.current?.disconnect();
        };
    }, []);

    const handleTextChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
        const newText = e.target.value;
        const ytext = ydocRef.current.getText('briefing');

        // This is a naive way to sync. For production, use y-monaco or similar.
        // But for a 'Holographic' demo, it works.
        ydocRef.current.transact(() => {
            ytext.delete(0, ytext.length);
            ytext.insert(0, newText);
        });
    };

    return (
        <div className="space-y-6">
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <div className="p-3 bg-noble-gold/10 border border-noble-gold/20 rounded-2xl">
                        <Users className="w-6 h-6 text-noble-gold" />
                    </div>
                    <div>
                        <h3 className="text-xl font-black text-white uppercase tracking-tighter">Liquid War Room</h3>
                        <p className="text-[10px] text-zinc-500 font-bold uppercase tracking-widest mt-0.5">Multi-User Strategic Synthesis</p>
                    </div>
                </div>

                <div className="flex items-center gap-4">
                    <div className="flex items-center gap-2 px-3 py-1.5 bg-blue-500/10 border border-blue-500/20 rounded-xl">
                        <Activity className="w-3 h-3 text-blue-400" />
                        <span className="text-[10px] text-blue-400 font-bold uppercase tracking-widest">{users} Active Peers</span>
                    </div>
                    <div className="flex items-center gap-2 px-3 py-1.5 bg-emerald-500/10 border border-emerald-500/20 rounded-xl">
                        <Shield className="w-3 h-3 text-emerald-400" />
                        <span className="text-[10px] text-emerald-400 font-bold uppercase tracking-widest">CRDT Encrypted</span>
                    </div>
                </div>
            </div>

            <Card className="p-1 bg-gradient-to-br from-white/5 to-transparent border-white/5 shadow-2xl overflow-hidden group">
                <div className="relative">
                    {/* Background Glow */}
                    <div className="absolute top-0 right-0 w-64 h-64 bg-blue-500/5 blur-[100px] pointer-events-none" />

                    <textarea
                        value={text}
                        onChange={handleTextChange}
                        placeholder="Begin collective strategic brief..."
                        className="w-full h-[400px] bg-black/40 backdrop-blur-md p-8 text-zinc-300 font-medium leading-relaxed outline-none border-none resize-none placeholder:text-zinc-800 selection:bg-blue-500/30"
                    />

                    {/* Cursor Simulation Overlay (Holographic Effect) */}
                    <div className="absolute bottom-6 right-8 flex items-center gap-3 opacity-30 group-hover:opacity-100 transition-opacity">
                        <div className="flex items-center gap-2 px-3 py-1 bg-zinc-900 border border-white/10 rounded-full">
                            <Zap size={10} className="text-amber-500" />
                            <span className="text-[8px] font-black text-zinc-500 uppercase tracking-widest">Liquid Sync Active</span>
                        </div>
                    </div>
                </div>
            </Card>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {[
                    { label: 'Latency', value: '12ms', icon: Activity },
                    { label: 'Consistency', value: 'Delta-Pass', icon: Shield },
                    { label: 'Network', value: 'Supabase-Mesh', icon: Zap }
                ].map((stat, i) => (
                    <div key={i} className="p-4 bg-zinc-900/40 border border-white/5 rounded-2xl flex items-center gap-4">
                        <stat.icon className="w-4 h-4 text-zinc-500" />
                        <div>
                            <p className="text-[8px] text-zinc-600 font-bold uppercase tracking-widest">{stat.label}</p>
                            <p className="text-xs text-zinc-300 font-black uppercase tracking-tighter">{stat.value}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Flame, Activity, Clock, Info } from 'lucide-react';
import { Card } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { cn } from '@/lib/utils';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';

// Mock data types interface
interface BurnoutMetric {
    id: string;
    label: string;
