}

// Mock Data Generator
const MOCK_STAFF: StaffMember[] = [
    {
        id: '1',
        name: 'Sarah Jenkins',
        role: 'Special Ed Lead',
        burnoutScore: 88,
        avatar: '/images/avatars/executive_leader.png',
        metrics: [
