            { id: 'm2', label: 'Compliance Fatigue', value: 30, trend: 'down', status: 'optimal', description: 'Recent paperwork completed' },
            { id: 'm3', label: 'Resource Access', value: 90, trend: 'stable', status: 'optimal', description: 'Full resource availability' }
        ]
    }
];

export default function BurnoutHeatmap() {
    const [selectedStaff, setSelectedStaff] = useState<StaffMember | null>(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Simulate data loading
        const timer = setTimeout(() => {
            setIsLoading(false);
        }, 1500);
        return () => clearTimeout(timer);
    }, []);

    const getStatusColor = (status: string) => {
        switch (status) {
            case 'critical': return 'text-red-500 bg-red-500/10 border-red-500/20';
            case 'warning': return 'text-amber-500 bg-amber-500/10 border-amber-500/20';
            default: return 'text-emerald-500 bg-emerald-500/10 border-emerald-500/20';
        }
    };

    const getScoreColor = (score: number) => {
        if (score >= 80) return 'text-red-500';
        if (score >= 60) return 'text-amber-500';
        return 'text-emerald-500';
    };

    const getProgressColor = (score: number) => {
        if (score >= 80) return 'bg-red-500';
        if (score >= 60) return 'bg-amber-500';
        return 'bg-emerald-500';
    };

    return (
        <Card className="w-full h-full bg-zinc-950 border-white/10 overflow-hidden flex flex-col relative group">
            <div className="absolute inset-0 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')] opacity-[0.02] pointer-events-none" />

            {/* Header */}
            <div className="p-6 border-b border-white/10 flex items-center justify-between relative z-10">
                <div className="flex items-center gap-3">
                    <div className="w-10 h-10 rounded-xl bg-red-500/10 border border-red-500/20 flex items-center justify-center">
                        <Flame className="w-5 h-5 text-red-500" />
                    </div>
                    <div>
                        <h3 className="text-lg font-bold text-white tracking-tight">Ecosystem Burnout Heatmap</h3>
                        <div className="flex items-center gap-2">
                            <span className="w-1.5 h-1.5 rounded-full bg-red-500 animate-pulse" />
                            <span className="text-[10px] text-zinc-400 font-mono uppercase tracking-widest">Live Biometric Telemetry</span>
                        </div>
                    </div>
                </div>
                <Badge variant="outline" className="bg-zinc-900/50 border-white/10 text-zinc-400">
                    <Clock className="w-3 h-3 mr-1" />
                    Updated: Just now
                </Badge>
            </div>

            {/* Main Content */}
            <div className="flex-1 p-6 relative z-10 overflow-y-auto custom-scrollbar">
                {isLoading ? (
                    <div className="h-full flex flex-col items-center justify-center space-y-4">
                        <div className="w-12 h-12 rounded-full border-2 border-red-500/20 border-t-red-500 animate-spin" />
                        <p className="text-xs text-zinc-500 font-mono animate-pulse">ANALYZING NEURAL STRESS MARKERS...</p>
                    </div>
                ) : (
                    <div className="space-y-6">
                        {/* High Level Stats */}
                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                            <div className="p-4 rounded-xl bg-white/5 border border-white/10">
                                <div className="text-xs text-zinc-400 uppercase tracking-wider mb-1">Avg. Strain</div>
                                <div className="text-2xl font-black text-white">65%</div>
                            </div>
                            <div className="p-4 rounded-xl bg-red-500/10 border border-red-500/20">
                                <div className="text-xs text-red-400 uppercase tracking-wider mb-1">Critical Risk</div>
                                <div className="text-2xl font-black text-red-500">2 Staff</div>
                            </div>
                            <div className="p-4 rounded-xl bg-emerald-500/10 border border-emerald-500/20">
                                <div className="text-xs text-emerald-400 uppercase tracking-wider mb-1">Optimal State</div>
                                <div className="text-2xl font-black text-emerald-500">8 Staff</div>
                            </div>
                        </div>

                        {/* Staff Heatmap List */}
                        <div className="space-y-3">
                            {MOCK_STAFF.map((staff) => (
                                <motion.div
                                    key={staff.id}
                                    initial={{ opacity: 0, y: 10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    className={cn(
                                        "p-4 rounded-xl border transition-all cursor-pointer hover:bg-white/5",
                                        selectedStaff?.id === staff.id
                                            ? "bg-white/5 border-noble-gold/30 shadow-[0_0_15px_rgba(197,164,126,0.1)]"
                                            : "bg-black/20 border-white/5 hover:border-white/10"
                                    )}
                                    onClick={() => setSelectedStaff(staff.id === selectedStaff?.id ? null : staff)}
                                >
                                    <div className="flex items-center gap-4">
                                        <div className="relative">
                                            <div className={cn(
                                                "w-12 h-12 rounded-full border-2 p-0.5",
                                                getScoreColor(staff.burnoutScore).replace('text-', 'border-')
                                            )}>
                                                <img src={staff.avatar} alt={staff.name} className="w-full h-full rounded-full object-cover grayscale group-hover:grayscale-0 transition-all" />
                                            </div>
                                            <div className={cn(
                                                "absolute -bottom-1 -right-1 w-5 h-5 rounded-full bg-zinc-900 flex items-center justify-center border border-white/10",
                                                getScoreColor(staff.burnoutScore)
                                            )}>
                                                <Activity size={10} />
                                            </div>
                                        </div>

                                        <div className="flex-1">
                                            <div className="flex items-center justify-between mb-1">
                                                <h4 className="font-bold text-white text-sm">{staff.name}</h4>
                                                <span className={cn("font-black text-sm", getScoreColor(staff.burnoutScore))}>
                                                    {staff.burnoutScore}%
                                                </span>
                                            </div>
                                            <p className="text-xs text-zinc-500 mb-2">{staff.role}</p>

                                            {/* Progress Bar */}
                                            <div className="h-1.5 w-full bg-zinc-800 rounded-full overflow-hidden">
                                                <motion.div
                                                    initial={{ width: 0 }}
                                                    animate={{ width: `${staff.burnoutScore}%` }}
                                                    className={cn("h-full rounded-full", getProgressColor(staff.burnoutScore))}
                                                />
                                            </div>
                                        </div>
                                    </div>

                                    {/* Expanded Details */}
                                    <AnimatePresence>
                                        {selectedStaff?.id === staff.id && (
                                            <motion.div
                                                initial={{ height: 0, opacity: 0 }}
                                                animate={{ height: 'auto', opacity: 1 }}
                                                exit={{ height: 0, opacity: 0 }}
                                                className="mt-4 pt-4 border-t border-white/5 overflow-hidden"
                                            >
                                                <div className="grid grid-cols-1 gap-2">
                                                    {staff.metrics.map((metric) => (
                                                        <div key={metric.id} className="flex items-center justify-between p-2 rounded-lg bg-black/30">
                                                            <div className="flex items-center gap-2">
                                                                <TooltipProvider>
                                                                    <Tooltip>
                                                                        <TooltipTrigger>
                                                                            <Info size={12} className="text-zinc-500 hover:text-white transition-colors" />
                                                                        </TooltipTrigger>
                                                                        <TooltipContent>
                                                                            <p>{metric.description}</p>
                                                                        </TooltipContent>
                                                                    </Tooltip>
                                                                </TooltipProvider>
                                                                <span className="text-xs text-zinc-300">{metric.label}</span>
                                                            </div>
                                                            <Badge variant="outline" className={cn("text-[10px] uppercase font-bold border-0", getStatusColor(metric.status))}>
                                                                {metric.status}
                                                            </Badge>
                                                        </div>
                                                    ))}
                                                </div>
                                                <div className="mt-4 flex gap-2">
                                                    <button className="flex-1 py-2 rounded-lg bg-noble-gold/10 text-noble-gold text-xs font-bold uppercase tracking-wider hover:bg-noble-gold/20 transition-colors border border-noble-gold/20">
                                                        Deploy Support
                                                    </button>
                                                    <button className="flex-1 py-2 rounded-lg bg-white/5 text-zinc-400 text-xs font-bold uppercase tracking-wider hover:bg-white/10 transition-colors border border-white/10">
                                                        View Full Profile
                                                    </button>
                                                </div>
                                            </motion.div>
                                        )}
                                    </AnimatePresence>
                                </motion.div>
                            ))}
                        </div>
                    </div>
                )}
            </div>
        </Card>
    );
}

'use client';
import { motion, AnimatePresence } from 'framer-motion';
import { Trophy, ChevronRight, Star } from 'lucide-react';
import { useState, useEffect } from 'react';
import useProfessionalSounds from '@/hooks/useProfessionalSounds';
import Confetti from 'react-confetti';

interface PromotionProps {
    rank: {
        title: string;
        level: number;
        color: string;
        clearance: string;
    };
    isOpen: boolean;
    onCloseAction: () => void;
}

export default function ProfessionalPromotion({ rank, isOpen, onCloseAction }: PromotionProps) {
    const { playSuccess } = useProfessionalSounds();
    const [showConfetti, setShowConfetti] = useState(false);

    useEffect(() => {
        if (isOpen) {
            playSuccess();
            setShowConfetti(true);
            const timer = setTimeout(() => setShowConfetti(false), 5000);
            return () => clearTimeout(timer);
        }
    }, [isOpen]);

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[500] flex items-center justify-center p-4">
                    {showConfetti && <Confetti recycle={false} numberOfPieces={500} colors={['#6366f1', '#a855f7', '#fbbf24']} />}

                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 bg-black/90 backdrop-blur-2xl"
                    />

                    <motion.div
                        initial={{ opacity: 0, scale: 0.8, rotateX: 20 }}
                        animate={{ opacity: 1, scale: 1, rotateX: 0 }}
                        exit={{ opacity: 0, scale: 1.1, rotateX: -20 }}
                        className="relative w-full max-w-xl text-center"
                    >
                        {/* Radiant Background FX */}
                        <div className="absolute inset-0 bg-indigo-500/20 blur-[120px] rounded-full animate-pulse" />

                        <div className="relative z-10 space-y-8">
                            <motion.div
                                initial={{ y: 50, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                transition={{ delay: 0.2 }}
                                className="inline-flex items-center gap-2 px-4 py-2 rounded-full bg-white/5 border border-white/10 text-indigo-400 text-[10px] font-black uppercase tracking-[0.3em]"
                            >
                                <Star size={12} fill="currentColor" /> Executive Ascension Detected
                            </motion.div>

                            <div className="space-y-2">
                                <motion.h2
                                    initial={{ scale: 0.5, opacity: 0 }}
                                    animate={{ scale: 1, opacity: 1 }}
                                    transition={{ type: "spring", stiffness: 200, damping: 15, delay: 0.3 }}
                                    className="text-6xl font-black text-white uppercase tracking-tighter"
                                >
                                    Promoted
                                </motion.h2>
                                <motion.p
                                    initial={{ y: 20, opacity: 0 }}
                                    animate={{ y: 0, opacity: 1 }}
                                    transition={{ delay: 0.5 }}
                                    className="text-zinc-500 font-mono text-xs uppercase tracking-[0.5em]"
                                >
                                    New Rank Calibrated
                                </motion.p>
                            </div>

                            <motion.div
                                initial={{ scale: 0, rotate: -45 }}
                                animate={{ scale: 1, rotate: 0 }}
                                transition={{ type: "spring", duration: 1, delay: 0.6 }}
                                className="w-48 h-48 mx-auto relative"
                            >
                                <div className="absolute inset-0 bg-indigo-500/20 rounded-full blur-2xl animate-pulse" />
                                <div className="absolute inset-0 border-2 border-dashed border-indigo-500/30 rounded-full animate-spin-slow" />
                                <div className="absolute inset-4 bg-gradient-to-br from-indigo-600 to-purple-600 rounded-[3rem] shadow-2xl flex items-center justify-center">
                                    <Trophy size={64} className="text-white drop-shadow-2xl" />
                                </div>
                            </motion.div>

                            <motion.div
                                initial={{ y: 40, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                transition={{ delay: 0.8 }}
                                className="space-y-4"
                            >
                                <div>
                                    <h3 className={`text-3xl font-black uppercase tracking-widest ${rank.color}`}>{rank.title}</h3>
                                    <p className="text-zinc-400 text-xs mt-2 uppercase tracking-widest font-bold">Clearance: {rank.clearance}</p>
                                </div>
                            </motion.div>

                            <motion.div
                                initial={{ y: 20, opacity: 0 }}
                                animate={{ y: 0, opacity: 1 }}
                                transition={{ delay: 1 }}
                                className="pt-8"
                            >
                                <button
                                    onClick={onCloseAction}
                                    className="group relative px-12 py-5 bg-white text-black font-black text-xs uppercase tracking-widest rounded-2xl hover:bg-indigo-500 hover:text-white transition-all shadow-2xl hover:shadow-indigo-500/50"
                                >
                                    Return to Command Deck <ChevronRight size={16} className="inline ml-2 group-hover:translate-x-1 transition-transform" />
                                </button>
                            </motion.div>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
}

'use client';

import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Shield, Radio, Activity, Brain } from 'lucide-react';
import dynamic from 'next/dynamic';

const Conversation = dynamic(() => import('./cvi/components/conversation').then(mod => mod.Conversation), { ssr: false });

interface EdIntelDirectorProps {
    cinematicVideoSrc?: string;
    tavusConversationUrl?: string;
    avatarName?: string;
    avatarRole?: string;
}

export default function EdIntelDirector({
    cinematicVideoSrc = "/videos/heritage/alvin_west_doctoral_intro.mp4",
    avatarName = "Dr. Alvin West",
    avatarRole = "Chief Executive Officer"
}: EdIntelDirectorProps) {
    const [mode, setMode] = useState<'cinematic' | 'live'>('cinematic');
    const [isShimmering, setIsShimmering] = useState(false);

    const handleTransition = () => {
        setIsShimmering(true);
        setTimeout(() => {
            setMode(mode === 'cinematic' ? 'live' : 'cinematic');
            setIsShimmering(false);
        }, 1500);
    };

    return (
        <div className="relative w-full h-[90vh] bg-black rounded-[2.5rem] border border-white/10 overflow-hidden shadow-2xl">
            {/* Neural Background Mesh */}
            <div className="absolute inset-0 bg-[linear-gradient(rgba(245,158,11,0.03)_1px,transparent_1px),linear-gradient(90deg,rgba(245,158,11,0.03)_1px,transparent_1px)] bg-[size:48px_48px] opacity-20 pointer-events-none" />

            {/* Shimmer Effect Layer */}
            <AnimatePresence>
                {isShimmering && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="absolute inset-0 z-[60] bg-white/20 backdrop-blur-3xl mix-blend-overlay"
                    >
                        <motion.div
                            className="absolute inset-0 bg-gradient-to-r from-transparent via-amber-500/50 to-transparent"
                            animate={{ x: ['-100%', '100%'] }}
                            transition={{ duration: 1, ease: "easeInOut" }}
                        />
                    </motion.div>
                )}
            </AnimatePresence>

            {/* Main Stage */}
            <div className="relative w-full h-full flex flex-col md:flex-row">
                <div className="flex-1 relative bg-black overflow-hidden">
                    <AnimatePresence mode="wait">
                        {mode === 'cinematic' ? (
                            <motion.div
                                key="cinematic"
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                exit={{ opacity: 0 }}
                                className="w-full h-full"
                            >
                                <video
                                    src={cinematicVideoSrc}
                                    autoPlay
                                    loop
                                    muted
                                    playsInline
                                    className="w-full h-full object-cover"
                                />
                                <div className="absolute inset-0 bg-gradient-to-t from-black/60 via-transparent to-black/30" />

                                {/* Heritage Label */}
                                <div className="absolute top-8 left-8 bg-black/40 backdrop-blur-md border border-amber-500/30 px-4 py-2 rounded-2xl">
                                    <div className="flex items-center gap-3">
                                        <div className="w-2 h-2 bg-amber-500 rounded-full animate-pulse" />
                                        <span className="text-xs font-black text-white uppercase tracking-widest">
                                            {avatarName} - Neural Introduction
                                        </span>
                                    </div>
                                </div>
                            </motion.div>
                        ) : (
                            <motion.div
                                key="live"
                                initial={{ opacity: 0 }}
                                animate={{ opacity: 1 }}
                                exit={{ opacity: 0 }}
                                className="w-full h-full"
                            >
                                <Conversation
                                    onLeave={() => setMode('cinematic')}
                                    style={{ border: 'none' }}
                                />
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                {/* Director Controls Sidebar */}
                <div className="w-full md:w-80 bg-zinc-900/50 backdrop-blur-3xl border-l border-white/10 p-8 flex flex-col">
                    <div className="mb-8">
                        <h2 className="text-2xl font-black text-white tracking-tighter mb-1 uppercase">EdIntel Director</h2>
                        <p className="text-[10px] text-amber-500 font-mono tracking-widest uppercase">Board Activation Mode</p>
                    </div>

                    <div className="flex-1 space-y-6">
                        <div className="space-y-2">
                            <span className="text-[9px] font-black text-zinc-500 uppercase tracking-widest">Uplink Status</span>
                            <div className="p-4 rounded-2xl bg-black/40 border border-white/5 flex items-center justify-between">
                                <span className="text-xs text-white font-medium">Phoenix-3</span>
                                <div className="flex items-center gap-2">
                                    <span className="text-[10px] text-emerald-500 font-bold uppercase">Ready</span>
                                    <div className="w-2 h-2 bg-emerald-500 rounded-full animate-pulse" />
                                </div>
                            </div>
                        </div>

                        <div className="space-y-4">
                            <span className="text-[9px] font-black text-zinc-500 uppercase tracking-widest">Neural Directives</span>
                            <button
                                onClick={handleTransition}
                                className={`w-full py-6 rounded-2xl font-black text-xs uppercase tracking-widest transition-all flex flex-col items-center gap-3 ${mode === 'cinematic'
                                    ? 'bg-gradient-to-br from-amber-500 to-orange-600 text-white shadow-lg shadow-amber-900/30 ring-1 ring-amber-400/50'
                                    : 'bg-zinc-800 text-zinc-400 border border-white/5'}`}
                            >
                                <Radio size={24} className={mode === 'cinematic' ? 'animate-pulse' : ''} />
                                Activate Live Q&A
                            </button>

                            <button className="w-full py-4 bg-white/5 border border-white/10 rounded-xl text-zinc-300 font-bold text-[10px] uppercase tracking-widest hover:bg-white/10 transition-all flex items-center justify-center gap-2">
                                <Brain size={16} />
                                Ground in Local History
                            </button>

                            <button className="w-full py-4 bg-white/5 border border-white/10 rounded-xl text-zinc-300 font-bold text-[10px] uppercase tracking-widest hover:bg-white/10 transition-all flex items-center justify-center gap-2">
                                <Shield size={16} />
                                Board Compliance Shield
                            </button>
                        </div>
                    </div>

                    {/* Biometric Telemetry */}
                    <div className="mt-auto pt-8 border-t border-white/5 space-y-4">
                        <div className="flex items-center justify-between">
                            <div className="flex flex-col">
                                <span className="text-[8px] text-zinc-500 font-bold uppercase">Neural Load</span>
                                <span className="text-xs text-white font-mono">14.2%</span>
                            </div>
                            <Activity size={16} className="text-amber-500 opacity-50" />
                        </div>
                        <div className="h-1 w-full bg-zinc-800 rounded-full overflow-hidden">
                            <motion.div
                                className="h-full bg-amber-500"
                                animate={{ width: ['10%', '15%', '12%'] }}
                                transition={{ duration: 2, repeat: Infinity }}
                            />
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

'use client';

import React, { useEffect, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { PhoneOff, Mic, MicOff, Video, VideoOff } from 'lucide-react';

interface ConversationProps {
    conversationUrl?: string;
