                    className={`p-4 rounded-2xl transition-all ${isVideoEnabled ? 'bg-white/10 text-white hover:bg-white/20' : 'bg-red-500 text-white'}`}
                >
                    {isVideoEnabled ? <Video size={20} /> : <VideoOff size={20} />}
