            </div>
        </Card>
    );
}

"use client";

import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Brain, Zap, Activity, Timer, Play, Pause, RefreshCw, Trophy, ChevronRight } from 'lucide-react';

/* -------------------------------------------------------------------------- */
/*                                Types & Stats                               */
/* -------------------------------------------------------------------------- */

type DrillType = 'working-memory' | 'inhibition' | 'flexibility';

interface DrillStats {
    score: number;
    streak: number;
    highScore: number;
    reactionTime: number[]; // ms
    focusScore: number; // 0-100 derived from consistency
}

/* -------------------------------------------------------------------------- */
/*                              Main Component                                */
/* -------------------------------------------------------------------------- */

export default function CognitiveGym() {
    const [activeDrill, setActiveDrill] = useState<DrillType | null>(null);
    const [isPlaying, setIsPlaying] = useState(false);
    const [stats, setStats] = useState<DrillStats>({
        score: 0,
        streak: 0,
        highScore: 0,
        reactionTime: [],
        focusScore: 85,
    });

    // --- Session State ---
    const [elapsedTime, setElapsedTime] = useState(0);

    useEffect(() => {
        let interval: NodeJS.Timeout;
        if (isPlaying) {
            interval = setInterval(() => {
                setElapsedTime(prev => prev + 1);
            }, 1000);
        }
        return () => clearInterval(interval);
    }, [isPlaying]);

    const formatTime = (seconds: number) => {
        const mins = Math.floor(seconds / 60);
        const secs = seconds % 60;
        return `${mins}:${secs.toString().padStart(2, '0')}`;
    };

    return (
        <div className="min-h-screen bg-[#020617] text-white font-sans selection:bg-purple-500/30 overflow-hidden relative">
            {/* Background Effects */}
            <div className="fixed inset-0 pointer-events-none">
                <div className="absolute top-0 left-0 w-full h-[600px] bg-gradient-to-b from-indigo-950/20 to-transparent opacity-50" />
                <div className="absolute bottom-0 right-0 w-[800px] h-[800px] bg-purple-900/10 rounded-full blur-[120px]" />
                {/* Neural Synapse Grid (Simulated with CSS) */}
                <div className="absolute inset-0 bg-[url('/grid.svg')] bg-center [mask-image:linear-gradient(180deg,white,rgba(255,255,255,0))] opacity-5" />
            </div>

            <div className="relative z-10 max-w-7xl mx-auto px-6 py-8 flex flex-col h-screen">

                {/* --- HEADER --- */}
                <header className="flex items-center justify-between mb-12 border-b border-white/5 pb-6">
                    <div>
                        <div className="flex items-center gap-2 mb-2">
                            <span className="text-[10px] font-black uppercase tracking-[0.3em] text-[#D4AF37]">Neural Architect</span>
                            <div className="h-px flex-1 w-12 bg-[#D4AF37]/50" />
                        </div>
                        <h1 className="text-4xl font-black tracking-tighter text-white flex items-center gap-3">
                            <Brain className="w-8 h-8 text-indigo-500" />
                            COGNITIVE GYM
                        </h1>
                    </div>

                    <div className="flex items-center gap-8">
                        <StatBlock label="Focus Score" value={stats.focusScore} unit="%" icon={Activity} color="text-emerald-400" />
                        <StatBlock label="Session" value={formatTime(elapsedTime)} icon={Timer} color="text-blue-400" />
                        <StatBlock label="Streak" value={stats.streak} icon={Zap} color="text-[#D4AF37]" />
                    </div>
                </header>

                {/* --- MAIN ARENA --- */}
                <main className="flex-1 flex flex-col items-center justify-center relative">
                    <AnimatePresence mode='wait'>
                        {!activeDrill ? (
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -20 }}
                                className="w-full max-w-5xl"
                            >
                                <h2 className="text-2xl font-bold mb-8 text-center text-zinc-400">Select Training Protocol</h2>
                                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                                    <DrillCard
                                        type="working-memory"
                                        title="N-Back Challenge"
                                        desc="Enhance working memory capacity by tracking sequential patterns."
                                        onClick={() => setActiveDrill('working-memory')}
                                    />
                                    <DrillCard
                                        type="inhibition"
                                        title="Stroop Effect"
                                        desc="Train impulse control and inhibition by resolving conflict."
                                        onClick={() => setActiveDrill('inhibition')}
                                    />
                                    <DrillCard
                                        type="flexibility"
                                        title="Task Switching"
                                        desc="Improve cognitive flexibility by adapting to changing rules."
                                        onClick={() => setActiveDrill('flexibility')}
                                    />
                                </div>
                            </motion.div>
                        ) : (
                            <ActiveDrillArena
                                type={activeDrill}
                                onExit={() => { setActiveDrill(null); setIsPlaying(false); }}
                                isPlaying={isPlaying}
                                setIsPlaying={setIsPlaying}
                                score={stats.score}
                                onScore={(points) => setStats(prev => ({ ...prev, score: prev.score + points, streak: prev.streak + 1 }))}
                                onMiss={() => setStats(prev => ({ ...prev, streak: 0 }))}
                            />
                        )}
                    </AnimatePresence>
                </main>

            </div>
        </div>
    );
}

/* -------------------------------------------------------------------------- */
/*                              Sub-Components                                */
/* -------------------------------------------------------------------------- */

function StatBlock({ label, value, unit, icon: Icon, color }: any) {
    return (
        <div className="text-right">
            <p className="text-[10px] font-bold text-zinc-500 uppercase tracking-wider mb-1 flex items-center justify-end gap-1">
                {label} <Icon className={`w-3 h-3 ${color}`} />
            </p>
            <p className="text-2xl font-black font-mono tracking-tight text-white">
                {value}<span className="text-sm text-zinc-600 ml-0.5">{unit}</span>
            </p>
        </div>
    );
}

function DrillCard({ type, title, desc, onClick }: any) {
    return (
        <button
            onClick={onClick}
            className="group relative p-8 rounded-3xl bg-zinc-900/40 border border-white/5 hover:border-indigo-500/50 hover:bg-zinc-900/60 transition-all duration-300 text-left hover:-translate-y-2 overflow-hidden"
        >
            <div className="absolute inset-0 bg-gradient-to-br from-indigo-500/0 to-purple-500/0 group-hover:from-indigo-500/10 group-hover:to-purple-500/5 transition-all" />

            <div className="relative z-10">
                <div className="w-12 h-12 rounded-xl bg-zinc-950 border border-white/10 flex items-center justify-center mb-6 group-hover:scale-110 transition-transform shadow-lg">
                    {type === 'working-memory' && <Brain className="w-6 h-6 text-emerald-400" />}
                    {type === 'inhibition' && <Activity className="w-6 h-6 text-rose-400" />}
                    {type === 'flexibility' && <RefreshCw className="w-6 h-6 text-amber-400" />}
                </div>

                <h3 className="text-xl font-bold text-white mb-2 group-hover:text-indigo-300 transition-colors">{title}</h3>
                <p className="text-sm text-zinc-400 leading-relaxed mb-6">{desc}</p>

                <div className="flex items-center text-[10px] font-black uppercase tracking-widest text-zinc-600 group-hover:text-white transition-colors">
                    Initialize Protocol <ChevronRight className="w-3 h-3 ml-1" />
                </div>
            </div>
        </button>
    );
}

/* -------------------------------------------------------------------------- */
/*                            Drill Implementations                           */
/* -------------------------------------------------------------------------- */

// --- STROOP MOCK ---
function ActiveDrillArena({ type, onExit, isPlaying, setIsPlaying, score, onScore, onMiss }: any) {
    const [currentStimulus, setCurrentStimulus] = useState<{ word: string, color: string } | null>(null);
    const [feedback, setFeedback] = useState<'correct' | 'miss' | null>(null);

    const COLORS = [
        { name: 'RED', hex: '#ef4444' },
        { name: 'BLUE', hex: '#3b82f6' },
        { name: 'GREEN', hex: '#22c55e' },
        { name: 'YELLOW', hex: '#eab308' },
    ];

    const generateStimulus = () => {
        const word = COLORS[Math.floor(Math.random() * COLORS.length)];
        const color = COLORS[Math.floor(Math.random() * COLORS.length)]; // Can be congruent or incongruent
        setCurrentStimulus({ word: word.name, color: color.hex });
    };

    useEffect(() => {
        if (isPlaying && !currentStimulus) {
            generateStimulus();
        }
    }, [isPlaying, currentStimulus]);

    const handleResponse = (selectedColor: string) => {
        if (!currentStimulus) return;

        // Stroop: Match the INK COLOR, not the word
        if (selectedColor === currentStimulus.color) {
            setFeedback('correct');
            onScore(100);
        } else {
            setFeedback('miss');
            onMiss();
        }

        setTimeout(() => {
            setFeedback(null);
            generateStimulus();
        }, 500);
    };

    if (!isPlaying) {
        return (
            <div className="text-center">
                <div className="w-24 h-24 rounded-full bg-indigo-500/20 flex items-center justify-center mx-auto mb-6 animate-pulse">
                    <Zap className="w-10 h-10 text-indigo-400" />
                </div>
                <h2 className="text-3xl font-black text-white mb-2">Protocol Ready</h2>
                <p className="text-zinc-400 mb-8 max-w-md mx-auto">
                    {type === 'inhibition' && "Tap the button matching the INK COLOR of the word shown."}
                    {type === 'working-memory' && "Memorize the sequence. Tap when the current item matches 2 steps back."}
                    {type === 'flexibility' && "Sort the number by Even/Odd or High/Low depending on the rule color."}
                </p>
                <div className="flex gap-4 justify-center">
                    <button onClick={onExit} className="px-8 py-3 rounded-xl border border-white/10 text-zinc-400 hover:bg-white/5 text-sm font-bold uppercase tracking-wider">
                        Abort
                    </button>
                    <button onClick={() => setIsPlaying(true)} className="px-8 py-3 rounded-xl bg-[#D4AF37] text-black hover:bg-amber-400 text-sm font-bold uppercase tracking-wider shadow-lg shadow-amber-500/20">
                        Start Session
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="w-full max-w-2xl text-center relative">
            <div className="absolute top-0 right-0 p-4 font-mono text-zinc-500">
                SCORE: <span className="text-white">{score}</span>
            </div>

            {/* FEEDBACK OVERLAY */}
            <AnimatePresence>
                {feedback && (
                    <motion.div
                        initial={{ opacity: 0, scale: 0.5 }}
                        animate={{ opacity: 1, scale: 1.2 }}
                        exit={{ opacity: 0 }}
                        className={`absolute inset-0 flex items-center justify-center pointer-events-none z-20 font-black text-6xl uppercase tracking-tighter ${feedback === 'correct' ? 'text-emerald-500' : 'text-rose-500'}`}
                    >
                        {feedback === 'correct' ? 'EXCELLENT' : 'MISMATCH'}
                    </motion.div>
                )}
            </AnimatePresence>

            {type === 'inhibition' && currentStimulus && (
                <div className="py-20">
                    <motion.div
                        key={currentStimulus.word + currentStimulus.color}
                        initial={{ scale: 0.8, opacity: 0 }}
                        animate={{ scale: 1, opacity: 1 }}
                        className="text-8xl font-black tracking-tighter mb-12 drop-shadow-2xl"
                        style={{ color: currentStimulus.color }}
                    >
                        {currentStimulus.word}
                    </motion.div>

                    <div className="grid grid-cols-2 gap-4 max-w-md mx-auto">
                        {COLORS.map((c) => (
                            <button
                                key={c.name}
                                onClick={() => handleResponse(c.hex)}
                                className="h-16 rounded-xl border border-white/10 hover:border-white/30 bg-white/5 hover:bg-white/10 transition-all font-bold tracking-widest text-zinc-300"
                            >
                                {c.name}
                            </button>
                        ))}
                    </div>
                </div>
            )}

            {/* Disclaimer for other modes in this mockup */}
            {(type === 'working-memory' || type === 'flexibility') && (
                <div className="py-20">
                    <p className="text-zinc-500 mb-8">Simulation Mode: Only 'Stroop Effect' is fully interactive in this demo.</p>
                    <button onClick={() => setActiveDrill('inhibition')} className="text-indigo-400 hover:text-indigo-300 underline">Switch to Stroop</button>
                </div>
            )}

            <button onClick={onExit} className="absolute bottom-[-100px] left-1/2 -translate-x-1/2 text-zinc-600 hover:text-zinc-400 text-xs uppercase tracking-widest">
                End Session
            </button>
        </div>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Loader2, Wand2, Download, Eye } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export interface InVideoCreatorProps {
    className?: string;
}

export function InVideoCreator({ className = '' }: InVideoCreatorProps) {
    const [prompt, setPrompt] = useState('');
    const [style, setStyle] = useState<'professional' | 'casual' | 'educational'>('educational');
    const [aspectRatio, setAspectRatio] = useState<'16:9' | '9:16' | '1:1'>('16:9');
    const [duration, setDuration] = useState(60);
    const [isCreating, setIsCreating] = useState(false);
    const [projectId, setProjectId] = useState<string | null>(null);
    const [videoUrl, setVideoUrl] = useState<string | null>(null);
    const [progress, setProgress] = useState(0);
    const [error, setError] = useState<string | null>(null);

    // Ref to hold the controller for the active request
    const abortControllerRef = useRef<AbortController | null>(null);

    // Cleanup on unmount
    useEffect(() => {
        return () => {
            if (abortControllerRef.current) {
                abortControllerRef.current.abort();
            }
        };
    }, []);

    const handleCreate = async () => {
        if (!prompt.trim()) {
            setError('Please enter a video description');
            return;
        }

        // Cancel previous request
        if (abortControllerRef.current) {
            abortControllerRef.current.abort();
        }

        const controller = new AbortController();
        abortControllerRef.current = controller;

        setIsCreating(true);
        setError(null);
        setProgress(0);

        try {
            const response = await fetch('/api/invideo/create', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    prompt,
                    style,
                    aspectRatio,
                    duration,
                    includeMusic: true,
                }),
                signal: controller.signal
            });

            if (!response.ok) {
                throw new Error('Failed to create video');
            }

            const data = await response.json();
            if (controller.signal.aborted) return;
            setProjectId(data.projectId);

            // Poll for completion with AbortSignal
            let attempts = 0;
            const maxAttempts = 120; // 10 minutes (5s interval)

            while (attempts < maxAttempts) {
                if (controller.signal.aborted) break;

                const statusResponse = await fetch(
                    `/api/invideo/create?projectId=${data.projectId}`,
                    { signal: controller.signal }
                );
                const status = await statusResponse.json();

                if (controller.signal.aborted) break;

                if (status.status === 'completed' && status.video_url) {
                    setVideoUrl(status.video_url);
                    setProgress(100);
                    setIsCreating(false);
                    return;
                } else if (status.status === 'failed') {
                    throw new Error('Video creation failed');
                } else {
                    setProgress(status.progress || Math.min(progress + 3, 95));
                }

                // Wait 5 seconds using a cancellable promise
                await new Promise((resolve, reject) => {
                    const timeout = setTimeout(resolve, 5000);
                    controller.signal.addEventListener('abort', () => {
                        clearTimeout(timeout);
                        reject(new DOMException('Aborted', 'AbortError'));
                    }, { once: true });
                });

                attempts++;
            }

            if (attempts >= maxAttempts && !controller.signal.aborted) {
                throw new Error('Video creation timeout');
            }

        } catch (err: any) {
            if (err.name === 'AbortError') {
                console.log('Video creation aborted');
                return;
            }
            setError(err instanceof Error ? err.message : 'Failed to create video');
            setIsCreating(false);
        } finally {
            if (abortControllerRef.current === controller) {
                abortControllerRef.current = null;
                // Typically don't reset isCreating here if successful, but handle logic above handles it
                if (!videoUrl && !error) setIsCreating(false);
            }
        }
    };

    return (
        <Card className={`p-6 bg-gradient-to-br from-slate-900 via-pink-900 to-slate-900 ${className}`}>
            <div className="space-y-6">
                {/* Header */}
                <div className="flex items-center gap-3">
                    <div className="p-3 bg-pink-500/20 rounded-lg">
                        <Wand2 className="w-6 h-6 text-pink-400" />
                    </div>
                    <div>
                        <h3 className="text-xl font-bold text-white">InVideo AI Creator</h3>
                        <p className="text-sm text-white/60">Generate complete videos from text prompts</p>
                    </div>
                </div>

                {/* Prompt Input */}
                <div className="space-y-2">
                    <label className="text-sm font-medium text-white/80">Video Description</label>
                    <textarea
                        value={prompt}
                        onChange={(e) => setPrompt(e.target.value)}
                        placeholder="Describe the video you want to create... E.g., 'Create an educational video about the water cycle for 5th graders with animations and clear explanations'"
                        className="w-full h-32 px-4 py-3 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-pink-500 resize-none"
                        disabled={isCreating}
                    />
                </div>

                {/* Settings */}
                <div className="grid grid-cols-3 gap-4">
                    <div className="space-y-2">
                        <label className="text-sm font-medium text-white/80">Style</label>
                        <select
                            value={style}
                            onChange={(e) => setStyle(e.target.value as any)}
                            className="w-full px-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-pink-500"
                            disabled={isCreating}
                        >
                            <option value="educational">Educational</option>
                            <option value="professional">Professional</option>
                            <option value="casual">Casual</option>
                        </select>
                    </div>

                    <div className="space-y-2">
                        <label className="text-sm font-medium text-white/80">Aspect Ratio</label>
                        <select
                            value={aspectRatio}
                            onChange={(e) => setAspectRatio(e.target.value as any)}
                            className="w-full px-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-pink-500"
                            disabled={isCreating}
                        >
                            <option value="16:9">16:9 (Landscape)</option>
                            <option value="9:16">9:16 (Portrait)</option>
                            <option value="1:1">1:1 (Square)</option>
                        </select>
                    </div>

                    <div className="space-y-2">
                        <label className="text-sm font-medium text-white/80">Duration (seconds)</label>
                        <input
                            type="number"
                            value={duration}
                            onChange={(e) => setDuration(Number(e.target.value))}
                            min={15}
                            max={300}
                            className="w-full px-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-pink-500"
                            disabled={isCreating}
                        />
                    </div>
                </div>

                {/* Create Button */}
                <Button
                    onClick={handleCreate}
                    disabled={isCreating || !prompt.trim()}
                    className="w-full bg-gradient-to-r from-pink-600 to-purple-600 hover:from-pink-700 hover:to-purple-700 text-white font-semibold py-3"
                >
                    {isCreating ? (
                        <>
                            <Loader2 className="w-5 h-5 mr-2 animate-spin" />
                            Creating... {progress}%
                        </>
                    ) : (
                        <>
                            <Wand2 className="w-5 h-5 mr-2" />
                            Create Video
                        </>
                    )}
                </Button>

                {/* Progress Bar */}
                <AnimatePresence>
                    {isCreating && (
                        <motion.div
                            initial={{ opacity: 0, height: 0 }}
                            animate={{ opacity: 1, height: 'auto' }}
                            exit={{ opacity: 0, height: 0 }}
                            className="space-y-2"
                        >
                            <div className="w-full bg-white/10 rounded-full h-2 overflow-hidden">
                                <motion.div
                                    className="h-full bg-gradient-to-r from-pink-500 to-purple-500"
                                    initial={{ width: 0 }}
                                    animate={{ width: `${progress}%` }}
                                    transition={{ duration: 0.5 }}
                                />
                            </div>
                            <p className="text-xs text-white/60 text-center">
                                AI is generating your video with visuals, voiceover, and music...
                            </p>
                        </motion.div>
                    )}
                </AnimatePresence>

                {/* Error Message */}
                {error && (
                    <div className="p-4 bg-red-500/20 border border-red-500/50 rounded-lg">
                        <p className="text-sm text-red-300">{error}</p>
                    </div>
                )}

                {/* Video Preview */}
                <AnimatePresence>
                    {videoUrl && (
                        <motion.div
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: 20 }}
                            className="space-y-4"
                        >
                            <div className="p-4 bg-green-500/20 border border-green-500/50 rounded-lg">
                                <p className="text-sm text-green-300 font-medium">
                                     Video created successfully!
                                </p>
                            </div>

                            <div className="aspect-video bg-black rounded-lg overflow-hidden">
                                <video
                                    src={videoUrl}
                                    controls
                                    className="w-full h-full"
                                >
                                    <track kind="captions" />
                                </video>
                            </div>

                            <div className="flex gap-2">
                                <Button
                                    onClick={() => window.open(videoUrl, '_blank')}
                                    className="flex-1 bg-white/10 hover:bg-white/20"
                                >
                                    <Eye className="w-4 h-4 mr-2" />
                                    View Full Screen
                                </Button>
                                <Button
                                    onClick={() => {
                                        const a = document.createElement('a');
                                        a.href = videoUrl;
                                        a.download = 'edintel-ai-video.mp4';
                                        a.click();
                                    }}
                                    className="flex-1 bg-white/10 hover:bg-white/20"
                                >
                                    <Download className="w-4 h-4 mr-2" />
                                    Download
                                </Button>
                            </div>
                        </motion.div>
                    )}
                </AnimatePresence>

                {/* Tips */}
                <div className="bg-white/5 rounded-lg p-4 space-y-2">
                    <p className="text-xs font-semibold text-white/80"> Tips for better results:</p>
                    <ul className="text-xs text-white/60 space-y-1 list-disc list-inside">
                        <li>Be specific about the topic and target audience</li>
                        <li>Mention desired visual style (animations, live footage, etc.)</li>
                        <li>Specify tone (formal, casual, energetic, calm)</li>
                        <li>Include key points you want covered</li>
                    </ul>
                </div>
            </div>
        </Card>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { useActions, useUIState } from '@ai-sdk/rsc';
import type { AI } from '@/app/ai-hub/ai';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Loader2, Brain, Send, Shield, Zap, Activity } from 'lucide-react';
import { motion } from 'framer-motion';
import AbilityAnimation from '@/components/shared/AbilityAnimation';

import { edgeAI } from '@/lib/ai/edge-ai';

export interface MetaAIChatProps {
    className?: string;
}

export function MetaAIChat({ className = '' }: MetaAIChatProps) {
    const [conversation, setConversation] = useUIState<typeof AI>();
    const { submitUserMessage } = useActions<typeof AI>();
    const [input, setInput] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [edgeStatus, setEdgeStatus] = useState<any>(null);
    const scrollRef = useRef<HTMLDivElement>(null);

    // Initialize Edge AI on mount
    useEffect(() => {
        edgeAI.init().catch(err => console.error('[Meta-AI] Edge Init Failed:', err));
    }, []);

    // Auto-scroll to bottom
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [conversation]);

    const handleSend = async () => {
        if (!input.trim() || isLoading) return;

        const userQuery = input;
        setInput('');
        setIsLoading(true);

        // 1. Perform Edge Verification (Transformers.js)
        const localCheck = await edgeAI.verifyEquity(userQuery);
        setEdgeStatus(localCheck);

        // Optimistically add user message with Edge Analytics
        setConversation((current: any) => [
            ...current,
            {
                id: Date.now().toString(),
                display: (
                    <div className="flex flex-col items-end w-full gap-2">
                        <div className="max-w-[85%] rounded-[2rem] px-6 py-4 bg-blue-600 text-white rounded-br-none border border-blue-400/30 shadow-xl">
                            <div className="flex items-center gap-2 mb-2 opacity-50">
                                <span className="text-[8px] font-black uppercase tracking-[0.2em]">Executive Intent</span>
                                <div className="w-1 h-1 rounded-full bg-white" />
                            </div>
                            <p className="text-sm font-bold">{userQuery}</p>
                        </div>
                        {localCheck && (
                            <div className="flex items-center gap-2 px-3 py-1 bg-white/5 border border-white/10 rounded-full">
                                <Activity size={8} className={localCheck.equityVerified ? 'text-emerald-500' : 'text-amber-500'} />
                                <span className="text-[7px] font-black uppercase tracking-widest text-zinc-500">
                                    Edge Verified: {localCheck.sentiment} ({Math.round(localCheck.confidence * 100)}%)
                                </span>
                            </div>
                        )}
                    </div>
                )
            }
        ]);

        try {
            const response = await submitUserMessage(userQuery);
            setConversation((current: any) => [...current, response]);
        } catch (error) {
            console.error('[Meta-AI] Transmission Error:', error);
            setConversation((current: any) => [
                ...current,
                {
                    id: Date.now().toString(),
                    display: <div className="text-red-500 text-xs italic p-4 bg-red-500/10 border border-red-500/20 rounded-xl">Neural Uplink Interrupted. Re-transmit directive.</div>
                }
            ]);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <Card className={`flex flex-col h-[700px] bg-black border-white/5 shadow-2xl relative overflow-hidden group/chat ${className}`}>
            {/* Background Ability Animation - Subtle */}
            <div className="absolute inset-0 opacity-[0.03] pointer-events-none">
                <AbilityAnimation type="communication" />
            </div>

            {/* Header */}
            <div className="p-6 border-b border-white/5 bg-zinc-900/40 backdrop-blur-xl relative z-20">
                <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                        <div className="relative">
                            <div className="absolute inset-0 bg-blue-500/20 blur-xl rounded-full animate-pulse" />
                            <div className="p-4 bg-blue-500/10 border border-blue-500/20 rounded-2xl relative">
                                <Brain className="w-8 h-8 text-blue-400" />
                            </div>
                        </div>
                        <div>
                            <div className="flex items-center gap-2">
                                <h3 className="text-2xl font-black text-white uppercase tracking-tighter">Meta AI</h3>
                                <div className="px-2 py-0.5 bg-blue-500/10 border border-blue-500/30 rounded text-[10px] text-blue-400 font-black uppercase tracking-widest">Llama 3.3</div>
                            </div>
                            <p className="text-xs text-zinc-500 font-medium uppercase tracking-widest mt-1 italic">EdIntel Neural Interface</p>
                        </div>
                    </div>

                    <div className="flex flex-col items-end gap-2">
                        <span className="text-[8px] text-zinc-600 font-bold uppercase tracking-[0.2em]">Compute Status</span>
                        <div className="flex items-center gap-2 px-3 py-1.5 bg-emerald-500/10 border border-emerald-500/20 rounded-xl">
                            <div className="w-1.5 h-1.5 rounded-full bg-emerald-500 animate-pulse" />
                            <span className="text-[10px] text-emerald-400 font-black uppercase tracking-widest">Holographic Active</span>
                        </div>
                    </div>
                </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-8 space-y-6 relative z-10 scrollbar-hide" ref={scrollRef}>
                {conversation.length === 0 && (
                    <div className="h-full flex flex-col items-center justify-center text-center space-y-6 py-12">
                        <motion.div
                            animate={{
                                scale: [1, 1.1, 1],
                                opacity: [0.3, 0.6, 0.3]
                            }}
                            transition={{ duration: 4, repeat: Infinity }}
                            className="p-8 bg-blue-500/5 rounded-full border border-blue-500/10"
                        >
                            <Zap className="w-16 h-16 text-blue-500/20" />
                        </motion.div>
                        <div className="space-y-2">
                            <p className="text-zinc-400 font-bold text-lg uppercase tracking-tight">System Ready</p>
                            <p className="text-zinc-600 text-xs max-w-xs uppercase tracking-widest leading-relaxed">Input strategic directive or educational inquiry to begin neural synthesis.</p>
                        </div>
                    </div>
                )}

                <div className="space-y-8">
                    {conversation.map((message: any) => (
                        <div key={message.id}>
                            {message.display}
                        </div>
                    ))}
                </div>

                {isLoading && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} className="flex justify-start">
                        <div className="bg-zinc-900/50 border border-white/5 rounded-[2rem] rounded-bl-none px-6 py-4 flex items-center gap-3">
                            <Loader2 className="w-4 h-4 animate-spin text-blue-500" />
                            <span className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest animate-pulse">Analyzing Pattern...</span>
                        </div>
                    </motion.div>
                )}
            </div>

            {/* Input Area */}
            <div className="p-8 border-t border-white/5 bg-zinc-900/20 relative z-20">
                <div className="flex gap-4 items-center">
                    <div className="flex-1 relative group">
                        <div className="absolute -inset-1 bg-gradient-to-r from-blue-500/20 to-indigo-500/20 rounded-2xl blur opacity-0 group-focus-within:opacity-100 transition-opacity" />
                        <input
                            type="text"
                            value={input}
                            onChange={(e) => setInput(e.target.value)}
                            onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
                            placeholder="TRANSMIT STRATEGIC QUERY..."
                            className="relative w-full px-6 py-5 bg-black border border-white/10 rounded-2xl text-white text-sm font-bold placeholder:text-zinc-700 outline-none focus:border-blue-500/50 transition-all uppercase tracking-wider"
                            disabled={isLoading}
                        />
                    </div>
                    <Button
                        onClick={handleSend}
                        disabled={isLoading || !input.trim()}
                        className="h-full px-8 py-5 bg-white text-black hover:bg-blue-500 hover:text-white rounded-2xl font-black uppercase text-xs tracking-[0.2em] transition-all shadow-[0_0_20px_rgba(255,255,255,0.1)] group"
                    >
                        {isLoading ? (
                            <Loader2 className="w-5 h-5 animate-spin" />
                        ) : (
                            <div className="flex items-center gap-3">
                                <span>Transmit</span>
                                <Send size={16} className="group-hover:translate-x-1 group-hover:-translate-y-1 transition-transform" />
                            </div>
                        )}
                    </Button>
                </div>
                <div className="mt-4 flex items-center justify-between px-2">
                    <div className="flex items-center gap-4">
                        <div className="flex items-center gap-1.5 text-[8px] font-black text-zinc-600 uppercase tracking-widest">
                            <Shield size={10} className="text-emerald-500" />
                            Secure Link Active
                        </div>
                        <div className="flex items-center gap-1.5 text-[8px] font-black text-zinc-600 uppercase tracking-widest">
                            <Activity size={10} className="text-blue-500" />
                            Latency: {isLoading ? 'Calculating...' : '42ms'}
                        </div>
                    </div>
                    <p className="text-[7px] font-mono text-zinc-700 uppercase tracking-[0.3em]">Llama-3.3-70B-Instruct-Turbo // EdIntel_Core_v4</p>
                </div>
            </div>
        </Card>
    );
}

import React, { useEffect, useRef, useState } from 'react';
import StreamingAvatar, { AvatarQuality, TaskType } from '@heygen/streaming-avatar';

interface AIAgentAvatarProps {
    textToSpeak?: string;
    className?: string;
}

export default function AIAgentAvatar({ textToSpeak = "", className = "" }: AIAgentAvatarProps) {
    const [avatar, setAvatar] = useState<StreamingAvatar | null>(null);
    const [streamReady, setStreamReady] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState<string | null>(null);
    const videoRef = useRef<HTMLVideoElement>(null);

    const handleSpeak = React.useCallback(async (text: string) => {
        if (avatar) {
            try {
                await avatar.speak({
                    text: text,
                    task_type: TaskType.REPEAT,
                });
            } catch (err) {
                console.error("Speak error:", err);
            }
        }
    }, [avatar]);

    useEffect(() => {
        if (avatar && streamReady && textToSpeak) {
            handleSpeak(textToSpeak);
        }
    }, [textToSpeak, avatar, streamReady, handleSpeak]);

    useEffect(() => {
        const controller = new AbortController();
        const signal = controller.signal;

        async function initAvatar() {
            try {
                setIsLoading(true);
                setError(null);
                // Fetch secure session token from backend
                const res = await fetch('/api/heygen-token', { signal });
                if (!res.ok) throw new Error('Failed to fetch protocol token');

                const data = await res.json();
                const token = data.token || data; // Handle different response shapes

                if (!token) throw new Error('Invalid protocol token');

                const newAvatar = new StreamingAvatar({ token });

                newAvatar.on('stream_ready', (event) => {
                    if (videoRef.current && event.detail) {
                        videoRef.current.srcObject = event.detail;
                        videoRef.current.onloadedmetadata = () => {
                            videoRef.current?.play().catch(console.error);
                            if (!signal.aborted) {
                                setStreamReady(true);
                                setIsLoading(false);
                            }
                        };
                    }
                });

                newAvatar.on('stream_disconnected', () => {
                    if (!signal.aborted) {
                        setStreamReady(false);
                    }
                });

                await newAvatar.createStartAvatar({
                    quality: AvatarQuality.High,
                    avatarName: 'default', // Future: Allow dynamic mapping to CORE_AVATARS
                });

                if (!signal.aborted) {
                    setAvatar(newAvatar);
                } else {
                    newAvatar.stopAvatar();
                }
            } catch (error: any) {
                if (signal.aborted) return;
                console.error("Failed to initialize avatar:", error);
                setError(error.message || "Protocol connection failed");
                setIsLoading(false);
            }
        }

        if (!avatar) {
            initAvatar();
        }

        return () => {
            controller.abort();
        };
    }, []); // Only run once on mount

    // Handle avatar cleanup separately
    useEffect(() => {
        return () => {
            if (avatar) {
                avatar.stopAvatar();
            }
        };
    }, [avatar]);

    return (
        <div className={`avatar-container relative w-full h-full overflow-hidden rounded-3xl border border-white/10 bg-black ${className}`}>
            {isLoading && (
                <div className="absolute inset-0 flex items-center justify-center bg-zinc-900 z-10">
                    <div className="flex flex-col items-center gap-4">
                        <div className="w-12 h-12 border-4 border-indigo-500/30 border-t-indigo-500 rounded-full animate-spin" />
                        <span className="text-[10px] font-black text-indigo-400 uppercase tracking-[0.3em]">Initializing Neural Link...</span>
                    </div>
                </div>
            )}

            {error && (
                <div className="absolute inset-0 flex items-center justify-center bg-zinc-900/90 z-20 p-6 text-center">
                    <div className="space-y-4">
                        <div className="text-red-500/50 flex justify-center">
                            <svg className="w-12 h-12" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={1} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-3L13.732 4c-.77-1.333-2.694-1.333-3.464 0L3.34 16c-.77 1.333.192 3 1.732 3z" />
                            </svg>
                        </div>
                        <p className="text-xs text-white/70 font-mono uppercase tracking-widest leading-relaxed">
                            {error}
                        </p>
                        <button
                            onClick={() => { setAvatar(null); setError(null); }}
                            className="px-4 py-2 bg-white/5 hover:bg-white/10 border border-white/10 rounded font-black text-[9px] text-white uppercase tracking-widest transition-all"
                        >
                            Retry Protocol
                        </button>
                    </div>
                </div>
            )}

            <video
                ref={videoRef}
                className="w-full h-full object-cover"
                autoPlay
                playsInline
                muted
            />

            {/* Interactive Controls Overlay */}
            <div className="absolute bottom-4 right-4 z-20 flex gap-2">
                {streamReady && (
                    <div className="px-2 py-1 bg-green-500/20 border border-green-500/30 rounded text-[10px] text-green-400 font-bold uppercase tracking-wider flex items-center gap-2">
                        <div className="w-1.5 h-1.5 rounded-full bg-green-500 animate-pulse" />
                        Live Stream
                    </div>
                )}
            </div>
        </div>
    );
}

