// Mock Data for National States
const NATIONAL_STATE_NODES = [
    { state: "Texas", schools: "8,900+", districts: "1,200", status: "Active", deployment: "92%" },
    { state: "Florida", schools: "4,200+", districts: "67", status: "Active", deployment: "88%" },
    { state: "Georgia", schools: "2,300+", districts: "181", status: "Active", deployment: "85%" },
    { state: "California", schools: "10,500+", districts: "1,000+", status: "Strategizing", deployment: "12%" },
    { state: "New York", schools: "4,800+", districts: "700+", status: "Active", deployment: "76%" },
    { state: "North Carolina", schools: "2,600+", districts: "115", status: "Active", deployment: "81%" },
    { state: "Virginia", schools: "2,100+", districts: "132", status: "Active", deployment: "79%" },
    { state: "Ohio", schools: "3,500+", districts: "600+", status: "Strategizing", deployment: "15%" },
    { state: "Illinois", schools: "4,000+", districts: "800+", status: "Active", deployment: "64%" },
    { state: "Pennsylvania", schools: "3,000+", districts: "500", status: "Active", deployment: "58%" },
];

export default function NetworkAtlas() {
    const [view, setView] = useState<'alabama' | 'national'>('alabama');
    const [searchTerm, setSearchTerm] = useState("");
    const [layout, setLayout] = useState<'grid' | 'list'>('grid');

    const filteredAlabama = useMemo(() =>
        ALABAMA_SCHOOL_NODES.filter(s =>
            s.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            s.district.toLowerCase().includes(searchTerm.toLowerCase())
        ), [searchTerm]);

    const filteredNational = useMemo(() =>
        NATIONAL_STATE_NODES.filter(n =>
            n.state.toLowerCase().includes(searchTerm.toLowerCase())
        ), [searchTerm]);

    return (
        <section className="py-32 bg-[#030303] relative overflow-hidden">
            {/* Background Atmosphere */}
            <div className="absolute inset-0 z-0">
                <div className="absolute top-0 right-0 w-[1000px] h-[1000px] bg-indigo-600/5 rounded-full blur-[180px]" />
                <div className="absolute bottom-0 left-0 w-[800px] h-[800px] bg-emerald-600/5 rounded-full blur-[150px]" />
                <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:40px_40px] opacity-[0.05]" />
            </div>

            <div className="relative z-10 max-w-7xl mx-auto px-6">
                <div className="flex flex-col lg:flex-row items-end justify-between mb-16 gap-8">
                    <div className="max-w-2xl">
                        <div className="inline-flex items-center gap-2 px-3 py-1 rounded-full bg-white/5 border border-white/10 text-zinc-400 text-[10px] font-bold uppercase tracking-[0.3em] mb-4">
                            <Globe size={12} className="text-indigo-500" />
                            <span>Global Strategic Atlas</span>
                        </div>
                        <h2 className="text-5xl md:text-8xl font-black text-white uppercase tracking-tighter leading-none mb-6">
                            Professional <br />
                            <span className="text-transparent bg-clip-text bg-gradient-to-r from-zinc-200 via-zinc-400 to-zinc-600 italic">
                                Intelligence Map.
                            </span>
                        </h2>
                        <p className="text-lg text-zinc-500 leading-relaxed font-light">
                            Real-time intelligence nodes spanning the Alabama educator network and expanding across 48 national jurisdictions. Access detailed school telemetry and regional deployment metrics.
                        </p>
                    </div>

                    <div className="flex flex-col sm:flex-row items-center gap-4 p-1.5 bg-zinc-900/50 border border-white/5 rounded-3xl backdrop-blur-xl">
                        <button
                            onClick={() => setView('alabama')}
                            className={`flex items-center gap-2 px-8 py-4 rounded-2xl text-[10px] font-black uppercase tracking-widest transition-all ${view === 'alabama' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-600/20' : 'text-zinc-500 hover:text-white'}`}
                        >
                            <MapPin size={14} />
                            Alabama Scan
                        </button>
                        <button
                            onClick={() => setView('national')}
                            className={`flex items-center gap-2 px-8 py-4 rounded-2xl text-[10px] font-black uppercase tracking-widest transition-all ${view === 'national' ? 'bg-indigo-600 text-white shadow-xl shadow-indigo-600/20' : 'text-zinc-500 hover:text-white'}`}
                        >
                            <Globe size={14} />
                            National Tactical
                        </button>
                    </div>
                </div>

                {/* Search & Tool Bar */}
                <div className="flex flex-col md:flex-row items-center gap-6 mb-12">
                    <div className="relative flex-1 group">
                        <div className="absolute inset-y-0 left-6 flex items-center pointer-events-none">
                            <Search className="w-5 h-5 text-zinc-600 group-focus-within:text-indigo-500 transition-colors" />
                        </div>
                        <input
                            type="text"
                            placeholder={view === 'alabama' ? "Deep Scan Alabama Schools (Name, District, NCES)..." : "Locate National Strategic Centers..."}
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="w-full pl-16 pr-6 py-5 bg-zinc-900 border border-white/5 rounded-2xl text-white focus:outline-none focus:border-indigo-500/50 transition-all font-medium placeholder:text-zinc-700"
                        />
                        <div className="absolute right-6 top-1/2 -translate-y-1/2 flex items-center gap-3">
                            <div className="h-6 w-px bg-white/5" />
                            <span className="text-[10px] font-bold text-zinc-600 uppercase tracking-widest">
                                {view === 'alabama' ? filteredAlabama.length : filteredNational.length} Results
                            </span>
                        </div>
                    </div>

                    <div className="flex items-center gap-4">
                        <div className="flex p-1 bg-zinc-900 border border-white/5 rounded-xl">
                            <button
                                onClick={() => setLayout('grid')}
                                className={`p-2 rounded-lg transition-all ${layout === 'grid' ? 'bg-white/5 text-white' : 'text-zinc-600 hover:text-white'}`}
                            >
                                <LayoutGrid size={18} />
                            </button>
                            <button
                                onClick={() => setLayout('list')}
                                className={`p-2 rounded-lg transition-all ${layout === 'list' ? 'bg-white/5 text-white' : 'text-zinc-600 hover:text-white'}`}
                            >
                                <List size={18} />
                            </button>
                        </div>
                        <button className="flex items-center gap-2 px-6 py-3.5 bg-white/5 border border-white/10 rounded-xl text-[10px] font-black text-white uppercase tracking-widest hover:bg-white/10 transition-all">
                            <Filter size={14} />
                            Advanced
                        </button>
                    </div>
                </div>

                {/* Atlas Data Display */}
                <div className="relative">
                    <AnimatePresence mode="wait">
                        <motion.div
                            key={`${view}-${layout}`}
                            initial={{ opacity: 0, scale: 0.99 }}
                            animate={{ opacity: 1, scale: 1 }}
                            exit={{ opacity: 0, scale: 1.01 }}
                            transition={{ duration: 0.4, ease: [0.16, 1, 0.3, 1] }}
                            className={layout === 'grid' ? "grid md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6" : "space-y-4"}
                        >
                            {view === 'alabama' ? (
                                filteredAlabama.map((school, idx) => (
                                    <AtlasCard
                                        key={idx}
                                        layout={layout}
                                        title={school.name}
                                        subtitle={school.district}
                                        type={school.type}
                                        metric={`${school.students} Students`}
                                        status={school.status}
                                        value={school.efficiency}
                                        icon={School}
                                        color="indigo"
                                    />
                                ))
                            ) : (
                                filteredNational.map((node, idx) => (
                                    <AtlasCard
                                        key={idx}
                                        layout={layout}
                                        title={node.state}
                                        subtitle={`${node.districts} Districts`}
                                        type="Regional HQ"
                                        metric={`${node.schools} Schools`}
                                        status={node.status}
                                        value={node.deployment}
                                        icon={Globe}
                                        color="emerald"
                                    />
                                ))
                            )}
                        </motion.div>
                    </AnimatePresence>

                    {/* Empty State */}
                    {(view === 'alabama' ? filteredAlabama.length : filteredNational.length) === 0 && (
                        <div className="py-32 text-center">
                            <div className="inline-flex p-6 rounded-full bg-white/5 border border-white/5 text-zinc-700 mb-6">
                                <Database size={40} />
                            </div>
                            <h3 className="text-xl font-bold text-white mb-2">Center Not Located.</h3>
                            <p className="text-zinc-500">The current search criteria did not match any active educational centers in our strategic atlas.</p>
                        </div>
                    )}
                </div>

                {/* Map Insight Banner */}
                <div className="mt-20 grid md:grid-cols-3 gap-8">
                    {[
                        { icon: Activity, label: "Live Connection Capacity", value: "99.98%", sub: "Global Center Stability" },
                        { icon: Target, label: "Search Indexing", value: "114,000+", sub: "US Educational Entities" },
                        { icon: Cpu, label: "Strategic Processing", value: "2.4ms", sub: "Atlas Scann Rate" }
                    ].map((stat, i) => (
                        <div key={i} className="flex items-center gap-6 p-8 rounded-[2rem] bg-zinc-900/30 border border-white/5">
                            <div className="p-4 bg-white/5 rounded-2xl">
                                <stat.icon className="w-6 h-6 text-zinc-500" />
                            </div>
                            <div>
                                <div className="text-[10px] font-black text-zinc-600 uppercase tracking-widest mb-1">{stat.label}</div>
                                <div className="text-2xl font-black text-white">{stat.value}</div>
                                <div className="text-[10px] text-zinc-700 font-bold uppercase tracking-tighter">{stat.sub}</div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
}

function AtlasCard({ layout, title, subtitle, type, metric, status, value, icon: Icon, color }: any) {
    const isGrid = layout === 'grid';
    const accentColor = color === 'indigo' ? 'text-indigo-400' : 'text-emerald-400';
    const bgColor = color === 'indigo' ? 'bg-indigo-500/10' : 'bg-emerald-500/10';
    const borderColor = color === 'indigo' ? 'border-indigo-500/20' : 'border-emerald-500/20';
    const hoverBorder = color === 'indigo' ? 'hover:border-indigo-500/40' : 'hover:border-emerald-500/40';

    if (!isGrid) {
        return (
            <div className={`p-4 rounded-2xl bg-zinc-900/40 border border-white/5 ${hoverBorder} transition-all flex items-center gap-6 group`}>
                <div className={`p-3 rounded-xl ${bgColor} border border-white/5 group-hover:scale-105 transition-transform`}>
                    <Icon className={`w-5 h-5 ${accentColor}`} />
                </div>
                <div className="flex-1 min-w-0">
                    <h4 className="text-white font-bold truncate">{title}</h4>
                    <p className="text-[10px] text-zinc-500 uppercase tracking-widest leading-none mt-1">{subtitle}</p>
                </div>
                <div className="hidden md:block w-32 px-4 border-l border-white/5">
                    <div className="text-[10px] text-zinc-600 uppercase tracking-[0.2em] mb-1">Type</div>
                    <div className="text-xs font-bold text-zinc-300">{type}</div>
                </div>
                <div className="hidden lg:block w-40 px-4 border-l border-white/5">
                    <div className="text-[10px] text-zinc-600 uppercase tracking-[0.2em] mb-1">Metric</div>
                    <div className="text-xs font-bold text-zinc-300">{metric}</div>
                </div>
                <div className="w-32 px-4 border-l border-white/5">
                    <div className="text-[10px] text-zinc-600 uppercase tracking-[0.2em] mb-1">Status</div>
                    <div className="flex items-center gap-2">
                        <div className={`w-1.5 h-1.5 rounded-full ${status === 'Operational' || status === 'Active' ? 'bg-emerald-500' : 'bg-amber-500'} animate-pulse`} />
                        <span className="text-[10px] font-bold text-white uppercase tracking-tighter">{status}</span>
                    </div>
                </div>
                <div className="w-24 text-right">
                    <div className="text-lg font-black text-white">{value}</div>
                    <div className="text-[8px] text-zinc-600 uppercase tracking-widest group-hover:text-zinc-400 transition-colors">Deployment</div>
                </div>
                <ChevronRight className="w-5 h-5 text-zinc-800 group-hover:text-white transition-colors" />
            </div>
        );
    }

    return (
        <div className={`group p-8 rounded-[2.5rem] bg-zinc-900/30 border border-white/5 ${hoverBorder} transition-all duration-500 relative overflow-hidden flex flex-col h-full`}>
            <div className="absolute top-0 right-0 p-8 opacity-5 group-hover:opacity-10 transition-opacity">
                <Icon size={120} className={accentColor} />
            </div>

            <div className="relative z-10 flex flex-col h-full">
                <div className="flex items-start justify-between mb-8">
                    <div className={`p-4 rounded-[1.5rem] ${bgColor} border border-white/5 group-hover:scale-110 transition-transform`}>
                        <Icon className={`w-6 h-6 ${accentColor}`} />
                    </div>
                    <div className="flex items-center gap-2 px-3 py-1 rounded-full bg-black/50 border border-white/5">
                        <div className={`w-1.5 h-1.5 rounded-full ${status === 'Operational' || status === 'Active' ? 'bg-emerald-500' : 'bg-amber-500'} animate-pulse shadow-[0_0_8px_rgba(16,185,129,0.5)]`} />
                        <span className="text-[9px] font-black text-zinc-400 uppercase tracking-widest">{status}</span>
                    </div>
                </div>

                <div className="mb-2">
                    <p className="text-[10px] font-black text-zinc-600 uppercase tracking-[0.3em] mb-1">{subtitle}</p>
                    <h4 className="text-2xl font-black text-white leading-tight group-hover:text-zinc-300 transition-colors uppercase italic">{title}</h4>
                </div>

                <p className="text-xs text-zinc-500 font-medium mb-8 flex-grow">{type}  {metric}</p>

                <div className="pt-6 border-t border-white/5 flex items-center justify-between">
                    <div className="flex items-center gap-4">
                        <div>
                            <div className="text-[9px] font-bold text-zinc-600 uppercase tracking-widest mb-1">Growth Index</div>
                            <div className="flex items-center gap-2">
                                <TrendingUp size={14} className="text-emerald-500" />
                                <span className="text-lg font-black text-white leading-none tracking-tighter">{value}</span>
                            </div>
                        </div>
                        <button className="px-3 py-1.5 rounded-lg bg-indigo-500/10 border border-indigo-500/20 text-[8px] font-black text-indigo-400 uppercase tracking-widest hover:bg-indigo-500/20 transition-all">
                            Strategic Consult
                        </button>
                    </div>
                    <button className="p-3 rounded-full bg-white/5 border border-white/5 text-zinc-400 hover:bg-white/10 hover:text-white transition-all transform group-hover:translate-x-1">
                        <ChevronRight size={18} />
                    </button>
                </div>
            </div>
        </div>
    );
}

'use client';
import { motion, AnimatePresence } from 'framer-motion';
import { Command, Zap, Shield, Globe, Users, FileText, ChevronRight, Terminal } from 'lucide-react';
import { useState, useEffect } from 'react';
import { useRouter } from 'next/navigation';
import useProfessionalSounds from '@/hooks/useProfessionalSounds';

export default function NexusCommand({ isOpen, onCloseAction, onActionAction }: { isOpen: boolean, onCloseAction: () => void, onActionAction: (action: string) => void }) {
    const [query, setQuery] = useState('');
    const [activeIndex, setActiveIndex] = useState(0);
    const { playClick, playHover } = useProfessionalSounds();
    const router = useRouter();

    const actions = [
        { id: 'broadcast', title: 'Broadcast Executive Directive', icon: Zap, color: 'text-amber-400', category: 'Communications' },
        { id: 'shield', title: 'Harden Policy Shield', icon: Shield, color: 'text-indigo-400', category: 'Defense' },
        { id: 'map', title: 'Deploy Territory Oversight', icon: Globe, color: 'text-emerald-400', category: 'Tactical' },
        { id: 'sarah', title: 'Consult Sarah Connors (Compliance)', icon: Users, color: 'text-rose-400', category: 'Delegates' },
        { id: 'west', title: 'Consult Dr. West (Vision)', icon: Users, color: 'text-purple-400', category: 'Delegates' },
        { id: 'vault', title: 'Access Intelligence Vault', icon: FileText, color: 'text-blue-400', category: 'Archive' },
    ];

    const filteredActions = actions.filter(action =>
        action.title.toLowerCase().includes(query.toLowerCase()) ||
        action.category.toLowerCase().includes(query.toLowerCase())
    );

    useEffect(() => {
        const handleKeyDown = (e: KeyboardEvent) => {
            if (e.key === 'ArrowDown') {
                setActiveIndex(prev => (prev + 1) % filteredActions.length);
            } else if (e.key === 'ArrowUp') {
                setActiveIndex(prev => (prev - 1 + filteredActions.length) % filteredActions.length);
            } else if (e.key === 'Enter') {
                if (filteredActions[activeIndex]) {
                    handleSelect(filteredActions[activeIndex]);
                }
            } else if (e.key === 'Escape') {
                onCloseAction();
            }
        };

        if (isOpen) {
            window.addEventListener('keydown', handleKeyDown);
            return () => window.removeEventListener('keydown', handleKeyDown);
        }
    }, [isOpen, activeIndex, filteredActions]);

    const handleSelect = (action: any) => {
        playClick();
        onActionAction(action.id);
        onCloseAction();
    };

    return (
        <AnimatePresence>
            {isOpen && (
                <div className="fixed inset-0 z-[300] flex items-start justify-center pt-[15vh] px-4">
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        onClick={onCloseAction}
                        className="absolute inset-0 bg-black/80 backdrop-blur-md"
                    />

                    <motion.div
                        initial={{ opacity: 0, scale: 0.95, y: -20 }}
                        animate={{ opacity: 1, scale: 1, y: 0 }}
                        exit={{ opacity: 0, scale: 0.95, y: -20 }}
                        className="w-full max-w-2xl bg-zinc-900/90 border border-white/10 rounded-2xl overflow-hidden shadow-2xl relative z-10"
                    >
                        {/* Search Input Area */}
                        <div className="p-5 border-b border-white/10 flex items-center gap-4 bg-black/20">
                            <Command size={20} className="text-indigo-500 animate-pulse" />
                            <input
                                autoFocus
                                type="text"
                                placeholder="TYPE A COMMAND OR SEARCH INTEL..."
                                value={query}
                                onChange={(e) => { setQuery(e.target.value); setActiveIndex(0); }}
                                className="w-full bg-transparent border-none text-white font-black uppercase tracking-widest text-sm focus:ring-0 outline-none placeholder:text-zinc-600"
                            />
                            <div className="flex items-center gap-1 px-2 py-1 rounded bg-white/5 border border-white/5 text-[10px] text-zinc-500 font-mono">
                                ESC
                            </div>
                        </div>

                        {/* Results Area */}
                        <div className="max-h-[60vh] overflow-y-auto p-2">
                            {filteredActions.length > 0 ? (
                                <div className="space-y-1">
                                    {filteredActions.map((action, i) => (
                                        <button
                                            key={action.id}
                                            onMouseEnter={() => { setActiveIndex(i); playHover(); }}
                                            onClick={() => handleSelect(action)}
                                            className={`w-full p-4 rounded-xl flex items-center justify-between transition-all group ${i === activeIndex ? 'bg-indigo-600 text-white' : 'hover:bg-white/5 text-zinc-400'
                                                }`}
                                        >
                                            <div className="flex items-center gap-4 text-left">
                                                <div className={`p-2 rounded-lg ${i === activeIndex ? 'bg-white/20 text-white' : `bg-black/40 ${action.color}`}`}>
                                                    <action.icon size={18} />
                                                </div>
                                                <div>
                                                    <div className={`text-[10px] font-black uppercase tracking-widest ${i === activeIndex ? 'text-white/80' : 'text-zinc-500'}`}>
                                                        {action.category}
                                                    </div>
                                                    <div className="font-bold text-sm tracking-tight">{action.title}</div>
                                                </div>
                                            </div>
                                            <div className="flex items-center gap-2">
                                                {i === activeIndex && (
                                                    <motion.div layoutId="arrow" className="flex items-center gap-2">
                                                        <span className="text-[10px] font-mono opacity-50 uppercase">Execute</span>
                                                        <ChevronRight size={14} />
                                                    </motion.div>
                                                )}
                                            </div>
                                        </button>
                                    ))}
                                </div>
                            ) : (
                                <div className="p-10 text-center">
                                    <Terminal size={40} className="mx-auto text-zinc-800 mb-4" />
                                    <p className="text-zinc-500 font-black uppercase tracking-widest text-[10px]">No Strategic Protocols Found</p>
                                </div>
                            )}
                        </div>

                        {/* Footer Help */}
                        <div className="p-4 border-t border-white/5 bg-black/40 flex justify-between items-center">
                            <div className="flex items-center gap-4">
                                <div className="flex items-center gap-1.5 text-[8px] font-mono text-zinc-500">
                                    <span className="px-1.5 py-0.5 rounded bg-white/5 border border-white/5 text-white"></span>
                                    <span>Navigate</span>
                                </div>
                                <div className="flex items-center gap-1.5 text-[8px] font-mono text-zinc-500">
                                    <span className="px-1.5 py-0.5 rounded bg-white/5 border border-white/5 text-white"></span>
                                    <span>Select</span>
                                </div>
                            </div>
                            <div className="text-[8px] font-black text-indigo-500 uppercase tracking-widest animate-pulse">
                                NEXUS_COMMAND_v1.0 ACTIVE
                            </div>
                        </div>
                    </motion.div>
                </div>
            )}
        </AnimatePresence>
    );
}

'use client';
import { useEffect, useState } from 'react';
import { BarChart, Bar, XAxis, YAxis, Tooltip, ResponsiveContainer, Cell } from 'recharts';

export default function RevenueDashboard() {
  const [mounted, setMounted] = useState(false);
  const [data, setData] = useState([
    { name: 'CLC', value: 490, color: '#0070f3' },
    { name: 'Vigor High', value: 245, color: '#7000ff' },
    { name: 'Whistler Elem', value: 147, color: '#00d1ff' },
    { name: 'Prichard MS', value: 98, color: '#52c41a' }
  ]);

  useEffect(() => {
    setMounted(true);
  }, []);

  if (!mounted) return <div className="w-full h-full p-6"><div className="h-[300px] w-full bg-zinc-100 dark:bg-zinc-800 rounded-xl animate-pulse" /></div>;

  return (
    <div className="w-full h-full">
      <h3 className="text-xl font-bold text-zinc-900 dark:text-white mb-6"> District Strategic Investment (Monthly)</h3>
      <div className="h-[300px]" style={{ width: '100%', height: 300, position: 'relative' }}>
        <ResponsiveContainer width="100%" height="100%">
          <BarChart data={data}>
            <XAxis dataKey="name" stroke="#71717a" fontSize={12} tickLine={false} axisLine={false} />
            <YAxis hide />
            <Tooltip
              contentStyle={{ backgroundColor: '#18181b', border: '1px solid #27272a', borderRadius: '12px', color: '#fff' }}
              itemStyle={{ color: '#fff' }}
              cursor={{ fill: 'transparent' }}
            />
            <Bar dataKey="value" radius={[8, 8, 8, 8]}>
              {data.map((entry, index) => (
                <Cell key={`cell-${index}`} fill={entry.color} strokeWidth={0} />
              ))}
            </Bar>
          </BarChart>
        </ResponsiveContainer>
      </div>
      <p className="text-xs text-zinc-500 text-center mt-6 font-mono bg-zinc-100 dark:bg-zinc-800 py-2 rounded-lg">
        Total Professional Tier Revenue: .00 | Net Strategic Impact: +22% Compliance
      </p>
    </div>
  );
}

'use client';

import { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { X, Play, Pause } from 'lucide-react';
import Image from 'next/image';

interface TalkingDelegateOverlayProps {
    isOpen: boolean;
    onClose: () => void;
    script: string;
    avatarImage: string;
    videoSrc?: string | null;
    name: string;
    role: string;
}

export default function TalkingDelegateOverlay({
    isOpen,
    onClose,
    script,
    avatarImage,
    videoSrc,
    name,
    role
}: TalkingDelegateOverlayProps) {
    const [audioSrc, setAudioSrc] = useState<string | null>(null);
    const [isLoading, setIsLoading] = useState(false);
    const [isPlaying, setIsPlaying] = useState(false);
    const [error, setError] = useState<string | null>(null);
    const audioRef = useRef<HTMLAudioElement | null>(null);

    // Jaw Animation State
    const [jawOpen, setJawOpen] = useState(false);

    // Fetch Audio when opened with new script (only if no video is provided)
    useEffect(() => {
        if (!isOpen || !script || videoSrc) return;

        const controller = new AbortController();

        const fetchAudio = async () => {
            setIsLoading(true);
            setError(null);
            setAudioSrc(null);

            try {
                const response = await fetch('/api/huggingface/tts', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ text: script.substring(0, 500) }), // Limit for demo speed
                    signal: controller.signal
                });

                if (!response.ok) throw new Error('Failed to synthesize speech');

                const blob = await response.blob();
                if (controller.signal.aborted) return;

                const url = URL.createObjectURL(blob);
                setAudioSrc(url);
                setIsLoading(false);

                // Auto-play
                setTimeout(() => {
                    if (audioRef.current && !controller.signal.aborted) {
                        audioRef.current.play().catch(e => console.error("Autoplay blocked", e));
                    }
                }, 500);

            } catch (err: any) {
                if (err.name === 'AbortError') return;
                console.error("TTS Error:", err);
                setError("Voice synthesis unavailable. Using backup protocol.");
                setIsLoading(false);
            }
        };

        fetchAudio();

        return () => {
            controller.abort();
            if (audioSrc) URL.revokeObjectURL(audioSrc);
        };
    }, [isOpen, script]); // Removed videoSrc from dependency to match logic (it's in the if check)

    // Lip-Sync Simulation (Randomized Jaw Movement when playing)
    useEffect(() => {
        let interval: NodeJS.Timeout;
        if (isPlaying) {
            interval = setInterval(() => {
                setJawOpen(prev => !prev);
            }, 150); // Fast toggle roughly matches speech cadence
        } else {
            setJawOpen(false);
        }
        return () => clearInterval(interval);
    }, [isPlaying]);

    return (
        <AnimatePresence>
            {isOpen && (
                <motion.div
                    initial={{ opacity: 0 }}
                    animate={{ opacity: 1 }}
                    exit={{ opacity: 0 }}
                    className="fixed inset-0 z-[200] flex items-end md:items-center justify-center md:justify-end p-4 md:p-10 pointer-events-none"
                >
                    <motion.div
                        initial={{ y: 100, scale: 0.9 }}
                        animate={{ y: 0, scale: 1 }}
                        exit={{ y: 100, scale: 0.9 }}
                        className="relative w-full max-w-md bg-zinc-950 border border-white/10 rounded-3xl overflow-hidden shadow-2xl pointer-events-auto"
                    >
                        {/* HEADER */}
                        <div className="absolute top-0 left-0 right-0 z-20 p-4 flex justify-between items-start bg-gradient-to-b from-black/80 to-transparent">
                            <div className="flex items-center gap-2 px-3 py-1 bg-black/60 backdrop-blur rounded-full border border-white/10">
                                <div className={`w-2 h-2 rounded-full ${isPlaying ? 'bg-emerald-500 animate-pulse' : 'bg-amber-500'}`} />
                                <span className="text-[10px] font-bold text-white uppercase tracking-wider">
                                    {isPlaying ? 'Live Audio Feed' : isLoading ? 'Synthesizing...' : 'Standby'}
                                </span>
                            </div>
                            <button
                                onClick={onClose}
                                className="p-2 rounded-full bg-black/40 hover:bg-white/10 text-white transition-colors border border-white/5"
                            >
                                <X size={16} />
                            </button>
                        </div>

                        {/* AVATAR DISPLAY */}
                        <div className="relative aspect-[4/5] w-full bg-zinc-900">
                            {/* Static Body / Video Feed */}
                            <div className="absolute inset-0 z-0">
                                {videoSrc ? (
                                    <video
                                        src={videoSrc}
                                        autoPlay
                                        loop
                                        muted={false}
                                        playsInline
                                        onPlay={() => setIsPlaying(true)}
                                        onPause={() => setIsPlaying(false)}
                                        onEnded={() => setIsPlaying(false)}
                                        className="w-full h-full object-cover"
                                    />
                                ) : (
                                    <Image
                                        src={avatarImage}
                                        alt={name}
                                        fill
                                        className="object-cover"
                                    />
                                )}
                            </div>

                            {/* "Talking" Overlay Effect 
                                We subtly scale the image on the Y axis to simulate speaking motion if we can't separate the jaw.
                                Or we use the opacity pulse.
                            */}
                            <motion.div
                                animate={{
                                    scaleY: isPlaying ? [1, 1.02, 1] : 1,
                                    scaleX: isPlaying ? [1, 1.01, 1] : 1
                                }}
                                transition={{ duration: 0.2, repeat: Infinity }}
                                className="absolute inset-0 z-0 mix-blend-overlay opacity-30 bg-indigo-500"
                            />

                            {/* Vignette */}
                            <div className="absolute inset-0 bg-gradient-to-t from-zinc-950 via-transparent to-transparent z-10" />

                            {/* Waveform Visualizer */}
                            {isPlaying && (
                                <div className="absolute bottom-32 left-1/2 -translate-x-1/2 z-20 flex items-end gap-1 h-8">
                                    {[...Array(8)].map((_, i) => (
                                        <motion.div
                                            key={i}
                                            className="w-1.5 bg-indigo-400 rounded-full shadow-[0_0_10px_rgba(129,140,248,0.8)]"
                                            animate={{ height: ['20%', '100%', '30%'] }}
                                            transition={{
                                                duration: 0.4 + Math.random() * 0.3,
                                                repeat: Infinity,
                                                delay: i * 0.05
                                            }}
                                        />
                                    ))}
                                </div>
                            )}
                        </div>

                        {/* CONTROLS AREA */}
                        <div className="absolute bottom-0 left-0 right-0 bg-zinc-950 p-6 z-20 border-t border-white/10">
                            <h3 className="text-xl font-black text-white uppercase tracking-tight mb-1">{name}</h3>
                            <p className="text-xs text-zinc-400 font-mono uppercase tracking-widest mb-4">{role}</p>

                            <div className="flex gap-3">
                                <button
                                    onClick={() => {
                                        if (videoSrc) {
                                            const v = document.querySelector('video');
                                            if (v) {
                                                if (isPlaying) v.pause();
                                                else v.play();
                                            }
                                        } else if (audioRef.current) {
                                            if (isPlaying) audioRef.current.pause();
                                            else audioRef.current.play();
                                        }
                                    }}
                                    disabled={isLoading || (!audioSrc && !videoSrc)}
                                    className="flex-1 py-3 bg-white text-black font-bold uppercase text-xs tracking-widest rounded-xl hover:bg-zinc-200 transition-colors flex items-center justify-center gap-2 disabled:opacity-50"
                                >
                                    {isPlaying ? <Pause size={14} /> : <Play size={14} />}
                                    {isPlaying ? 'Pause Protocol' : 'Resume Protocol'}
                                </button>
                            </div>
                        </div>

                        <audio
                            ref={audioRef}
                            src={audioSrc || ''}
                            onPlay={() => setIsPlaying(true)}
                            onPause={() => setIsPlaying(false)}
                            onEnded={() => setIsPlaying(false)}
                            onError={(e) => console.error("Audio Playback Error", e)}
                            className="hidden"
                        />
                    </motion.div>
                </motion.div>
            )}
        </AnimatePresence>
    );
}

'use client';

import React, { useState, useRef } from 'react';
import { motion } from 'framer-motion';
import { Camera, Upload, ShieldCheck, Loader2, CheckCircle2, ChevronRight } from 'lucide-react';

export default function VisualIEPScanner() {
    const [isScanning, setIsScanning] = useState(false);
    const [scannedText, setScannedText] = useState<string | null>(null);
    const [previewUrl, setPreviewUrl] = useState<string | null>(null);
    const fileInputRef = useRef<HTMLInputElement>(null);

    const handleFileChange = async (e: React.ChangeEvent<HTMLInputElement>) => {
        const file = e.target.files?.[0];
        if (!file) return;

        setPreviewUrl(URL.createObjectURL(file));
        setIsScanning(true);

        try {
            const formData = new FormData();
            formData.append('image', file);

            const res = await fetch('/api/google/vision', {
                method: 'POST',
                body: formData
            });

            if (!res.ok) throw new Error('Scan failed');
            const data = await res.ok ? await res.json() : { text: "IEP Draft 2026: Student mastery in Literacy Vector exceeds standard. Recommend tier promotion." };

            setScannedText(data.text);
        } catch (err) {
            console.error(err);
            setScannedText("ERROR: Neural Link Interrupted. Falling back to local OCR simulation...");
        } finally {
            setIsScanning(false);
        }
    };

    return (
        <div className="w-full max-w-xl bg-zinc-950 border border-noble-gold/20 rounded-[2.5rem] overflow-hidden shadow-2xl">
            <div className="p-8 border-b border-white/5 bg-gradient-to-r from-noble-gold/10 to-transparent flex justify-between items-center">
                <div>
                    <h3 className="text-xl font-black text-white uppercase tracking-tighter">Optical <span className="text-noble-gold">Core Scanner</span></h3>
                    <p className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest mt-1">Google Visual Intelligence Active</p>
                </div>
                <div className="w-10 h-10 rounded-xl bg-noble-gold/10 border border-noble-gold/20 flex items-center justify-center text-noble-gold">
                    <Camera size={20} />
                </div>
            </div>

            <div className="p-8 space-y-6">
                {!previewUrl ? (
                    <div
                        onClick={() => fileInputRef.current?.click()}
                        className="aspect-video rounded-3xl border-2 border-dashed border-white/10 hover:border-noble-gold/40 hover:bg-white/5 transition-all cursor-pointer flex flex-col items-center justify-center gap-4 group"
                    >
                        <div className="w-16 h-16 rounded-full bg-white/5 flex items-center justify-center group-hover:scale-110 transition-transform">
                            <Upload className="text-zinc-500 group-hover:text-noble-gold" />
                        </div>
                        <div className="text-center">
                            <p className="text-sm font-bold text-zinc-300 uppercase tracking-widest">Identify physical media</p>
                            <p className="text-[10px] text-zinc-600 mt-1 uppercase">Supports PDFs, PNGs, JPEGs</p>
                        </div>
                        <input
                            type="file"
                            ref={fileInputRef}
                            onChange={handleFileChange}
                            accept="image/*,.pdf"
                            className="hidden"
                        />
                    </div>
                ) : (
                    <div className="space-y-6">
                        <div className="relative aspect-video rounded-3xl overflow-hidden border border-white/10">
                            <img src={previewUrl} className="w-full h-full object-cover blur-[1px] opacity-50" />
                            <div className="absolute inset-0 bg-black/40 flex items-center justify-center">
                                {isScanning ? (
                                    <div className="flex flex-col items-center gap-4">
                                        <Loader2 className="w-12 h-12 text-noble-gold animate-spin" />
                                        <p className="text-xs font-black text-white uppercase tracking-[0.3em] animate-pulse">Scanning Neural Mesh...</p>
                                    </div>
                                ) : (
                                    <div className="text-center">
                                        <CheckCircle2 className="w-12 h-12 text-emerald-500 mx-auto mb-4" />
                                        <p className="text-xs font-black text-white uppercase tracking-[0.3em]">Analysis Complete</p>
                                    </div>
                                )}
                            </div>
                            {/* Scanning Light Logic */}
                            {isScanning && (
                                <motion.div
                                    className="absolute left-0 right-0 h-1 bg-noble-gold shadow-[0_0_20px_#d4af37] z-20"
                                    animate={{ top: ["0%", "100%", "0%"] }}
                                    transition={{ duration: 2, repeat: Infinity, ease: "linear" }}
                                />
                            )}
                        </div>

                        {scannedText && (
                            <motion.div
                                initial={{ opacity: 0, y: 20 }}
                                animate={{ opacity: 1, y: 0 }}
                                className="p-6 rounded-3xl bg-white/5 border border-white/5 space-y-4"
                            >
                                <div className="flex items-center justify-between">
                                    <h4 className="text-[10px] font-black text-zinc-500 uppercase tracking-widest">Extracted Intelligence</h4>
                                    <ShieldCheck size={14} className="text-emerald-500" />
                                </div>
                                <p className="text-xs text-zinc-200 leading-relaxed font-mono">
                                    {scannedText.substring(0, 300)}...
                                </p>
                                <button className="w-full py-4 rounded-xl bg-noble-gold text-black text-[10px] font-black uppercase tracking-[0.2em] flex items-center justify-center gap-2 hover:scale-[1.02] active:scale-[0.98] transition-all">
                                    Map to Strategic Protocol <ChevronRight size={14} />
                                </button>
                            </motion.div>
                        )}

                        <button
                            onClick={() => { setPreviewUrl(null); setScannedText(null); }}
                            className="text-[10px] font-black text-zinc-600 uppercase tracking-widest hover:text-white transition-colors block mx-auto"
                        >
                            Reset Optical Link
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
}

'use client';
import { useState, useEffect, useCallback } from 'react';
import {
    Brain,
    Zap,
    Target,
    Clock,
    ShieldCheck,
    Heart,
    RefreshCw,
    Eye,
    Cpu,
    ZapOff
} from 'lucide-react';
import { useIntelligence } from '@/context/IntelligenceContext';

type GameMode = 'memory' | 'inhibition' | 'logic' | 'speed';

export default function LeadershipGym({ onXPAction }: { onXPAction?: (amount: number) => void }) {
    const [mode, setMode] = useState<GameMode>('memory');
    const [gameActive, setGameActive] = useState(false);
    const [score, setScore] = useState(0);
    const [level, setLevel] = useState(1);
    const [timeLeft, setTimeLeft] = useState(30);

    // Memory Game State
    const [sequence, setSequence] = useState<number[]>([]);
    const [userInput, setUserInput] = useState<number[]>([]);
    const [isFlashing, setIsFlashing] = useState(false);
    const [activeColor, setActiveColor] = useState<number | null>(null);

    // Inhibition Game State
    const [stroopWord, setStroopWord] = useState('');
    const [stroopColor, setStroopColor] = useState('');

    // Speed Game State
    const [mathProblem, setMathProblem] = useState({ q: '', a: 0 });
    const [mathOptions, setMathOptions] = useState<number[]>([]);

    // Logic Game State
    const [pattern, setPattern] = useState<number[]>([]);
    const [patternMissing, setPatternMissing] = useState(0);
    const [patternOptions, setPatternOptions] = useState<number[]>([]);

    const colors = [
        { id: 0, name: 'Red', bg: 'bg-red-500', text: 'text-red-500' },
        { id: 1, name: 'Blue', bg: 'bg-blue-500', text: 'text-blue-500' },
        { id: 2, name: 'Green', bg: 'bg-green-500', text: 'text-green-500' },
        { id: 3, name: 'Yellow', bg: 'bg-yellow-500', text: 'text-yellow-500' }
    ];

    const startNewRound = useCallback(() => {
        if (mode === 'memory') {
            const newLength = 2 + level;
            const newSeq = Array.from({ length: newLength }, () => Math.floor(Math.random() * 4));
            setSequence(newSeq);
            setUserInput([]);
            flashSequence(newSeq);
        } else if (mode === 'inhibition') {
            const wordIdx = Math.floor(Math.random() * 4);
            const colorIdx = Math.floor(Math.random() * 4);
            setStroopWord(colors[wordIdx].name);
            setStroopColor(colors[colorIdx].bg);
        } else if (mode === 'speed') {
            const a = Math.floor(Math.random() * (level * 5)) + 1;
            const b = Math.floor(Math.random() * (level * 5)) + 1;
            const ans = a + b;
            setMathProblem({ q: `${a} + ${b}`, a: ans });
            const options = [ans, ans + 1, ans - 1, ans + 2].sort(() => Math.random() - 0.5);
            setMathOptions(options);
        } else if (mode === 'logic') {
            const start = Math.floor(Math.random() * 10);
            const step = Math.floor(Math.random() * 5) + 1;
            const fullPattern = [start, start + step, start + (step * 2), start + (step * 3)];
            const missingIdx = Math.floor(Math.random() * 4);
            const missingVal = fullPattern[missingIdx];

            setPattern(fullPattern);
            setPatternMissing(missingIdx);

            const options = [missingVal, missingVal + 1, missingVal - 1, missingVal + step].sort(() => Math.random() - 0.5);
            setPatternOptions(options);
        }
    }, [mode, level]);

    const flashSequence = async (seq: number[]) => {
        setIsFlashing(true);
        for (const colorId of seq) {
            setActiveColor(colorId);
            await new Promise(r => setTimeout(r, 600));
            setActiveColor(null);
            await new Promise(r => setTimeout(r, 200));
        }
        setIsFlashing(false);
    };

    const startGame = () => {
        setGameActive(true);
        setScore(0);
        setLevel(1);
        setTimeLeft(30);
        startNewRound();
    };

    const handleInput = (val: any) => {
        if (!gameActive || isFlashing) return;

        if (mode === 'memory') {
            const colorId = val as number;
            const newInput = [...userInput, colorId];
            setUserInput(newInput);
            if (colorId !== sequence[newInput.length - 1]) {
                setGameActive(false);
            } else if (newInput.length === sequence.length) {
                setScore(score + 10);
                setLevel(level + 1);
                setTimeout(startNewRound, 500);
            }
        } else if (mode === 'inhibition') {
            const selectedColorName = val as string;
            // The goal is to click the word, or the color? Let's say click the COLOR of the text.
            const colorName = colors.find(c => c.bg === stroopColor)?.name;
            if (selectedColorName === colorName) {
                setScore(score + 5);
                startNewRound();
            } else {
                setScore(Math.max(0, score - 2));
                startNewRound();
            }
        } else if (mode === 'speed') {
            if (val === mathProblem.a) {
                setScore(score + 5);
                if (score > 0 && score % 20 === 0) setLevel(level + 1);
                startNewRound();
            } else {
                setScore(Math.max(0, score - 2));
                startNewRound();
            }
        } else if (mode === 'logic') {
            if (val === pattern[patternMissing]) {
                setScore(score + 15);
                startNewRound();
                if (onXPAction) onXPAction(2);
            } else {
                setScore(Math.max(0, score - 5));
                startNewRound();
            }
        }
    };

    const { triggerBriefing } = useIntelligence();

    useEffect(() => {
        if (gameActive && timeLeft > 0) {
            const timer = setTimeout(() => setTimeLeft(timeLeft - 1), 1000);
            return () => clearTimeout(timer);
        } else if (timeLeft === 0) {
            setGameActive(false);
        }
    }, [gameActive, timeLeft]);

    return (
        <div className="p-10 rounded-[2.5rem] bg-gradient-to-br from-zinc-950 via-cyan-950 to-zinc-950 text-white shadow-3xl border border-cyan-900/50 relative overflow-hidden group">
            {/* Background Effects */}
            <div className="absolute top-0 left-0 w-full h-full bg-[radial-gradient(circle_at_50%_50%,rgba(6,182,212,0.1),transparent)] pointer-events-none" />

            <div className="relative z-10">
                <div className="flex items-center justify-between mb-10">
                    <div className="flex items-center gap-5">
                        <div className="w-16 h-16 rounded-3xl bg-cyan-600/20 border border-cyan-500/30 flex items-center justify-center shadow-xl shadow-cyan-900/20">
                            <Brain className="text-cyan-400" size={32} />
                        </div>
                        <div>
                            <h2 className="text-3xl font-black tracking-tighter">Leadership Workout</h2>
                            <p className="text-sm font-bold text-cyan-400 uppercase tracking-widest">Mental Performance Exercises</p>
                        </div>
                    </div>
                    <div className="flex items-center gap-4">
                        <button
                            onClick={() => triggerBriefing('Legacy Profile')}
                            className="flex items-center gap-2 px-4 py-2 rounded-xl bg-amber-500/10 border border-amber-500/20 text-amber-400 hover:bg-amber-500/20 transition-all group"
                        >
                            <Target size={14} className="group-hover:rotate-45 transition-transform" />
                            <span className="text-[10px] font-black uppercase tracking-widest">Founder Hub</span>
                        </button>
                        <div className="flex gap-4">
                            <div className="px-5 py-3 rounded-2xl bg-zinc-900 border border-zinc-800 text-center min-w-[80px]">
                                <div className="text-2xl font-black text-yellow-400 leading-none mb-1">{score}</div>
                                <div className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest">Score</div>
                            </div>
                            <div className="px-5 py-3 rounded-2xl bg-zinc-900 border border-zinc-800 text-center min-w-[80px]">
                                <div className="text-2xl font-black text-purple-400 leading-none mb-1">{level}</div>
                                <div className="text-[10px] font-bold text-zinc-500 uppercase tracking-widest">Stage</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Mode Selector */}
                <div className="grid grid-cols-4 gap-3 mb-10">
                    {[
                        { id: 'memory', label: 'Retention', icon: <Cpu size={16} /> },
                        { id: 'inhibition', label: 'Focus Check', icon: <ZapOff size={16} /> },
                        { id: 'speed', label: 'Quick Math', icon: <Zap size={16} /> },
                        { id: 'logic', label: 'Pattern Finder', icon: <Eye size={16} /> }
                    ].map((m) => (
                        <button
                            key={m.id}
                            onClick={() => { setMode(m.id as GameMode); setGameActive(false); }}
                            className={`flex flex-col items-center gap-2 p-4 rounded-2xl border transition-all ${mode === m.id
                                ? 'bg-cyan-500/20 border-cyan-500 text-cyan-300 shadow-lg shadow-cyan-900/20'
                                : 'bg-zinc-900/50 border-zinc-800 text-zinc-500 hover:border-zinc-700'
                                }`}
                        >
                            {m.icon}
                            <span className="text-[10px] font-bold uppercase tracking-wider text-center">{m.label}</span>
                        </button>
                    ))}
                </div>

                {/* Game Area */}
                <div className="min-h-[300px] flex flex-col items-center justify-center p-8 rounded-[2rem] bg-black/40 border border-zinc-900 relative">
                    {!gameActive ? (
                        <div className="text-center space-y-6">
                            <h3 className="text-xl font-bold text-zinc-300">
                                {mode === 'memory' && "Sequence Mastery"}
                                {mode === 'inhibition' && "Color Challenge"}
                                {mode === 'speed' && "Math Sprints"}
                                {mode === 'logic' && "Pattern Match"}
                            </h3>
                            <p className="text-sm text-zinc-500 max-w-sm mx-auto">
                                {mode === 'memory' && "Improve your working memory by recalling increasing sequences."}
                                {mode === 'inhibition' && "Click the NAME of the color, ignoring the text color. Improves decision making."}
                                {mode === 'speed' && "Solve rapid math equations to increase processing speed."}
                                {mode === 'logic' && "Identify the missing piece of the pattern. Improves abstract reasoning."}
                            </p>
                            <button
                                onClick={startGame}
                                className="px-10 py-5 bg-gradient-to-r from-cyan-600 to-blue-600 rounded-2xl font-black text-sm uppercase tracking-[0.2em] shadow-2xl shadow-cyan-900/40 hover:scale-105 transition-all flex items-center gap-3 mx-auto relative overflow-hidden group"
                            >
                                <span className="relative z-10 flex items-center gap-3">
                                    <RefreshCw size={18} />
                                    Start Workout
                                </span>
                                <div className="absolute inset-0 bg-white/20 translate-x-[-100%] group-hover:translate-x-[100%] transition-transform duration-700" />
                            </button>
                        </div>
                    ) : (
                        <div className="w-full h-full flex flex-col items-center justify-center">
                            <div className="absolute top-6 right-8 flex items-center gap-2 text-orange-500">
                                <Clock size={18} />
                                <span className="text-2xl font-black font-mono">{timeLeft}s</span>
                            </div>

                            {mode === 'memory' && (
                                <div className="grid grid-cols-2 gap-6 w-full max-w-xs">
                                    {colors.map((c) => (
                                        <button
                                            key={c.id}
                                            onClick={() => handleInput(c.id)}
                                            className={`h-24 rounded-3xl transition-all transform active:scale-95 border-4 ${activeColor === c.id ? `bg-white border-white scale-110` : `${c.bg} border-transparent opacity-60`
                                                }`}
                                        />
                                    ))}
                                </div>
                            )}

                            {mode === 'inhibition' && (
                                <div className="text-center space-y-12">
                                    <div className="text-7xl font-black uppercase tracking-tighter p-4 transition-all duration-300">
                                        <span className={stroopColor.replace('bg-', 'text-')}>{stroopWord}</span>
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {colors.map((c) => (
                                            <button
                                                key={c.id}
                                                onClick={() => handleInput(c.name)}
                                                className="px-8 py-5 rounded-2xl bg-zinc-900 border border-zinc-800 text-sm font-bold hover:bg-zinc-800 hover:border-cyan-500/50 transition-all uppercase tracking-[0.2em] shadow-lg"
                                            >
                                                {c.name}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {mode === 'speed' && (
                                <div className="text-center space-y-12">
                                    <div className="text-7xl font-black text-cyan-400 font-mono tracking-tighter">
                                        {mathProblem.q}
                                    </div>
                                    <div className="grid grid-cols-2 gap-4">
                                        {mathOptions.map((opt, i) => (
                                            <button
                                                key={i}
                                                onClick={() => handleInput(opt)}
                                                className="px-10 py-6 rounded-2xl bg-zinc-900 border border-zinc-800 text-2xl font-black hover:bg-zinc-800 transition-all text-zinc-300"
                                            >
                                                {opt}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {mode === 'logic' && (
                                <div className="text-center space-y-12">
                                    <div className="flex items-center gap-6 justify-center">
                                        {pattern.map((p, i) => (
                                            <div key={i} className={`w-20 h-20 rounded-2xl flex items-center justify-center text-4xl font-black ${i === patternMissing ? 'bg-zinc-800 border-2 border-dashed border-cyan-500/50 text-cyan-500 animate-pulse' : 'bg-zinc-900 border border-white/5 text-white'}`}>
                                                {i === patternMissing ? '?' : p}
                                            </div>
                                        ))}
                                    </div>
                                    <div className="grid grid-cols-2 gap-4 max-w-xs mx-auto">
                                        {patternOptions.map((opt, i) => (
                                            <button
                                                key={i}
                                                onClick={() => handleInput(opt)}
                                                className="px-8 py-5 rounded-2xl bg-zinc-900 border border-zinc-800 text-xl font-black hover:border-cyan-500 transition-all"
                                            >
                                                {opt}
                                            </button>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    )}
                </div>

                {/* Dashboard Stats */}
                <div className="mt-10 grid grid-cols-3 gap-6">
                    <StatItem label="Impulse Control" value="+42%" icon={<Target size={14} />} color="text-emerald-400" />
                    <StatItem label="Working Memory" value="+67%" icon={<Brain size={14} />} color="text-cyan-400" />
                    <StatItem label="Reaction Speed" value="-120ms" icon={<Zap size={14} />} color="text-orange-400" />
                </div>

                {/* Professional Centers Grid */}
                <div className="mt-12">
                    <p className="text-[10px] font-black text-zinc-600 uppercase tracking-[0.3em] mb-6 text-center">Stakeholder Cognitive Hubs</p>
                    <div className="grid grid-cols-2 lg:grid-cols-4 gap-4">
                        <TrainingCenter
                            title="Student Focus"
                            description="Executive function & recall"
                            color="cyan"
                            icon={<Brain size={14} />}
                            onClick={() => triggerBriefing('Student Focus')}
                        />
                        <TrainingCenter
                            title="Teacher Guard"
                            description="Burnout shield & focus"
                            color="blue"
                            icon={<ShieldCheck size={14} />}
                            onClick={() => triggerBriefing('Teacher Guard')}
                        />
                        <TrainingCenter
                            title="Admin Vision"
                            description="Strategic decision velocity"
                            color="purple"
                            icon={<Target size={14} />}
                            onClick={() => triggerBriefing('Admin Vision')}
                        />
                        <TrainingCenter
                            title="Parent Sync"
                            description="Cognitive patience & calm"
                            color="orange"
                            icon={<Heart size={14} />}
                            onClick={() => triggerBriefing('Parent Sync')}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

function StatItem({ label, value, icon, color }: { label: string, value: string, icon: React.ReactNode, color: string }) {
    return (
        <div className="p-5 rounded-2xl bg-zinc-950 border border-zinc-900 flex flex-col items-center">
            <div className={`mb-2 ${color} opacity-80`}>{icon}</div>
            <div className={`text-2xl font-black ${color}`}>{value}</div>
            <div className="text-[9px] font-bold text-zinc-600 uppercase tracking-widest">{label}</div>
        </div>
    );
}

function TrainingCenter({ title, description, color, icon, onClick }: { title: string, description: string, color: string, icon: React.ReactNode, onClick?: () => void }) {
    const colorClasses: Record<string, string> = {
        cyan: 'border-cyan-500/20 hover:border-cyan-500/50 bg-cyan-500/5 hover:shadow-[0_0_20px_rgba(34,211,238,0.1)]',
        blue: 'border-blue-500/20 hover:border-blue-500/50 bg-blue-500/5 hover:shadow-[0_0_20px_rgba(59,130,246,0.1)]',
        purple: 'border-purple-500/20 hover:border-purple-500/50 bg-purple-500/5 hover:shadow-[0_0_20px_rgba(168,85,247,0.1)]',
        orange: 'border-orange-500/20 hover:border-orange-500/50 bg-orange-500/5 hover:shadow-[0_0_20px_rgba(249,115,22,0.1)]',
    };

    return (
        <button
            onClick={onClick}
            className={`p-5 rounded-2xl border backdrop-blur-sm ${colorClasses[color]} transition-all duration-300 text-left group overflow-hidden relative hover:scale-[1.02]`}
        >
            <div className="relative z-10">
                <div className="flex items-center gap-2 mb-2">
                    <div className="opacity-60 group-hover:opacity-100 transition-opacity duration-300">{icon}</div>
                    <span className="text-[10px] font-black uppercase tracking-widest leading-none text-white/90">{title}</span>
                </div>
                <p className="text-[9px] text-zinc-500 leading-tight group-hover:text-zinc-300 transition-colors duration-300">{description}</p>
            </div>
            <div className="absolute inset-0 bg-gradient-to-br from-white/[0.02] to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-500 rounded-2xl" />
        </button>
    );
}

