'use client';

import { useState } from 'react';
import { Card, CardHeader, CardTitle, CardContent } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Shield, Send, Sparkles, MessageSquare } from 'lucide-react';
import { ScrollArea } from '@/components/ui/scroll-area';

export function BurnoutShield() {
    const [input, setInput] = useState('');
    const [messages, setMessages] = useState<{ role: 'user' | 'tiffany'; content: string }[]>([
        { role: 'tiffany', content: "I am here, Educator. What is weighing on your spirit today? Lay it down." }
    ]);
    const [loading, setLoading] = useState(false);

    const handleSend = async () => {
        if (!input.trim()) return;

        const userMsg = input;
        setInput('');
        setMessages(prev => [...prev, { role: 'user', content: userMsg }]);
        setLoading(true);

        try {
            const res = await fetch('/api/tiffany/burnout-shield', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ message: userMsg })
            });
            const data = await res.json();
            setMessages(prev => [...prev, { role: 'tiffany', content: data.response }]);
        } catch (error) {
            console.error("Failed to fetch response");
        } finally {
            setLoading(false);
        }
    };

    return (
        <Card className="flex flex-col h-[500px] border-indigo-200 bg-white shadow-sm dark:border-indigo-900 dark:bg-zinc-900">
            <CardHeader className="bg-indigo-50/50 pb-4 dark:bg-indigo-950/20">
                <CardTitle className="flex items-center gap-2 text-indigo-900 dark:text-indigo-100">
                    <Shield className="h-5 w-5 text-indigo-600 dark:text-indigo-400" />
                    Tiffany Peer-Review (Burnout Shield)
                </CardTitle>
                <p className="text-xs text-zinc-500 dark:text-zinc-400">
                    Sovereign protection for your peace of mind.
                </p>
            </CardHeader>
            <CardContent className="flex-1 flex flex-col p-4 gap-4 overflow-hidden">
                <ScrollArea className="flex-1 pr-4">
                    <div className="space-y-4">
                        {messages.map((msg, i) => (
                            <div
                                key={i}
                                className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}
                            >
                                <div
                                    className={`max-w-[85%] rounded-lg p-3 text-sm leading-relaxed
                                    ${msg.role === 'user'
                                            ? 'bg-zinc-100 text-zinc-800 dark:bg-zinc-800 dark:text-zinc-200'
                                            : 'bg-indigo-600 text-white shadow-md dark:bg-indigo-700'
                                        }`}
                                >
                                    {msg.role === 'tiffany' && <Sparkles className="h-3 w-3 mb-1 opacity-70" />}
                                    {msg.content}
                                </div>
                            </div>
                        ))}
                        {loading && (
                            <div className="flex justify-start">
                                <div className="bg-indigo-50 text-indigo-800 rounded-lg p-3 text-xs flex items-center gap-2 dark:bg-indigo-900/30 dark:text-indigo-300">
                                    <div className="h-2 w-2 bg-indigo-500 rounded-full animate-bounce" />
                                    Tiffany is listening...
                                </div>
                            </div>
                        )}
                    </div>
                </ScrollArea>

                <div className="flex gap-2 pt-2 border-t border-zinc-100 dark:border-zinc-800">
                    <input
                        className="flex-1 bg-transparent text-sm outline-none placeholder:text-zinc-400 dark:text-zinc-200"
                        placeholder="Vent here..."
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && handleSend()}
                        disabled={loading}
                    />
                    <Button
                        size="sm"
                        variant="primary"
                        onClick={handleSend}
                        disabled={loading || !input.trim()}
                        className="bg-indigo-600 hover:bg-indigo-700 text-white"
                    >
                        <Send className="h-4 w-4" />
                    </Button>
                </div>
            </CardContent>
        </Card>
    );
}

'use client';

import { useEffect, useRef, useState, useCallback } from 'react';
import StreamingAvatar, {
    AvatarQuality,
    StreamingEvents,
    TaskType,
    VoiceEmotion,
} from '@heygen/streaming-avatar';
import { Button } from '@/components/ui/button';
import { Card } from '@/components/ui/card';
import { Loader2, Mic, MicOff, Video, VideoOff, Volume2, VolumeX } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export interface StreamingAvatarProps {
    avatarId?: string;
    voiceId?: string;
    quality?: AvatarQuality;
    className?: string;
    onReady?: () => void;
    onError?: (error: Error) => void;
    autoStart?: boolean;
}

export function HeyGenStreamingAvatar({
    avatarId = 'default',
    voiceId = 'en-US-JennyNeural',
    quality = AvatarQuality.High,
    className = '',
    onReady,
    onError,
    autoStart = false,
}: StreamingAvatarProps) {
    const [isLoadingSession, setIsLoadingSession] = useState(false);
    const [isLoadingRepeat, setIsLoadingRepeat] = useState(false);
    const [stream, setStream] = useState<MediaStream | null>(null);
    const [debug, setDebug] = useState<string>('');
    const [data, setData] = useState<any>(null);
    const [text, setText] = useState<string>('');
    const [isSessionActive, setIsSessionActive] = useState(false);
    const [isMuted, setIsMuted] = useState(false);
    const [isVideoEnabled, setIsVideoEnabled] = useState(true);

    const mediaStream = useRef<HTMLVideoElement>(null);
    const avatar = useRef<StreamingAvatar | null>(null);
    const abortControllerRef = useRef<AbortController | null>(null);

    // ============================================
    // SESSION MANAGEMENT
    // ============================================

    async function fetchAccessToken(signal?: AbortSignal) {
        try {
            const response = await fetch('/api/heygen/streaming-token', {
                method: 'POST',
                signal
            });

            if (!response.ok) {
                throw new Error('Failed to fetch access token');
            }

            const { token } = await response.json();
            return token;
        } catch (error) {
            console.error('Error fetching access token:', error);
            throw error;
        }
    }

    async function startSession() {
        if (isLoadingSession) return;

        // Cancel any existing session init
        if (abortControllerRef.current) {
            abortControllerRef.current.abort();
        }

        const controller = new AbortController();
        abortControllerRef.current = controller;

        setIsLoadingSession(true);
        setDebug('Initializing session...');

        try {
            const newToken = await fetchAccessToken(controller.signal);

            if (controller.signal.aborted) return;

            avatar.current = new StreamingAvatar({
                token: newToken,
            });

            // Set up event listeners
            avatar.current.on(StreamingEvents.AVATAR_START_TALKING, (e) => {
                console.log('Avatar started talking', e);
                setDebug('Avatar is speaking...');
            });

            avatar.current.on(StreamingEvents.AVATAR_STOP_TALKING, (e) => {
                console.log('Avatar stopped talking', e);
                setDebug('Avatar finished speaking');
            });

            avatar.current.on(StreamingEvents.STREAM_DISCONNECTED, () => {
                console.log('Stream disconnected');
                setDebug('Session ended');
                endSession();
            });

            avatar.current.on(StreamingEvents.STREAM_READY, (event) => {
                if (controller.signal.aborted) return;
                console.log('Stream ready:', event.detail);
                setDebug('Session started successfully');
                setStream(event.detail);
                setIsSessionActive(true);
                if (onReady) onReady();
            });

            avatar.current.on(StreamingEvents.USER_START, (event) => {
                console.log('User started talking', event);
                setDebug('Listening...');
            });

            avatar.current.on(StreamingEvents.USER_STOP, (event) => {
                console.log('User stopped talking', event);
                setDebug('Processing...');
            });

            // Create streaming session
            const res = await avatar.current.createStartAvatar({
                quality,
                avatarName: avatarId,
                voice: {
                    voiceId: voiceId,
                    rate: 1.0,
                    emotion: VoiceEmotion.FRIENDLY,
                },
                language: 'en',
                disableIdleTimeout: false,
            });

            if (controller.signal.aborted) {
                await avatar.current.stopAvatar();
                return;
            }

            setData(res);
            setDebug('Session created');
        } catch (error: any) {
            if (error.name === 'AbortError') {
                setDebug('Session initialization aborted');
                return;
            }
            console.error('Error starting session:', error);
            setDebug(`Error: ${error instanceof Error ? error.message : 'Unknown error'}`);
            if (onError && error instanceof Error) {
                onError(error);
            }
        } finally {
            if (abortControllerRef.current === controller) {
                setIsLoadingSession(false);
                abortControllerRef.current = null;
            }
        }
    }

    async function endSession() {
        if (!avatar.current) {
            setDebug('No session to end');
            return;
        }

        try {
            await avatar.current.stopAvatar();
            setStream(null);
            setIsSessionActive(false);
            setDebug('Session ended');
        } catch (error) {
            console.error('Error ending session:', error);
            setDebug(`Error ending session: ${error instanceof Error ? error.message : 'Unknown error'}`);
        }
    }

    // ============================================
    // AVATAR INTERACTION
    // ============================================

    async function handleSpeak() {
        if (!avatar.current || !text.trim()) {
            setDebug('No avatar session or empty text');
            return;
        }

        setIsLoadingRepeat(true);
        setDebug('Sending message...');

        try {
            await avatar.current.speak({
                text: text,
                taskType: TaskType.REPEAT,
            });
            setText('');
            setDebug('Message sent');
        } catch (error) {
            console.error('Error speaking:', error);
            setDebug(`Error: ${error instanceof Error ? error.message : 'Unknown error'}`);
        } finally {
            setIsLoadingRepeat(false);
        }
    }

    async function handleInterrupt() {
        if (!avatar.current) {
            setDebug('No avatar session');
            return;
        }

        try {
            await avatar.current.interrupt();
            setDebug('Interrupted');
        } catch (error) {
            console.error('Error interrupting:', error);
            setDebug(`Error: ${error instanceof Error ? error.message : 'Unknown error'}`);
        }
    }

    const toggleMute = useCallback(() => {
        if (mediaStream.current) {
            mediaStream.current.muted = !isMuted;
            setIsMuted(!isMuted);
        }
    }, [isMuted]);

    const toggleVideo = useCallback(() => {
        if (mediaStream.current) {
            if (isVideoEnabled) {
                mediaStream.current.style.display = 'none';
            } else {
                mediaStream.current.style.display = 'block';
            }
            setIsVideoEnabled(!isVideoEnabled);
        }
    }, [isVideoEnabled]);

    // ============================================
    // EFFECTS
    // ============================================

    useEffect(() => {
        if (stream && mediaStream.current) {
            mediaStream.current.srcObject = stream;
            mediaStream.current.onloadedmetadata = () => {
                mediaStream.current!.play();
                setDebug('Playing');
            };
        }
    }, [stream]);

    useEffect(() => {
        if (autoStart) {
            startSession();
        }

        return () => {
            endSession();
        };
    }, [autoStart, startSession]);

    // ============================================
    // RENDER
    // ============================================

    return (
        <Card className={`relative overflow-hidden bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 ${className}`}>
            {/* Video Stream */}
            <div className="relative aspect-video bg-black rounded-lg overflow-hidden">
                <video
                    ref={mediaStream}
                    autoPlay
                    playsInline
                    className="w-full h-full object-cover"
                >
                    <track kind="captions" />
                </video>

                {/* Loading Overlay */}
                <AnimatePresence>
                    {(isLoadingSession || isLoadingRepeat) && (
                        <motion.div
                            initial={{ opacity: 0 }}
                            animate={{ opacity: 1 }}
                            exit={{ opacity: 0 }}
                            className="absolute inset-0 flex items-center justify-center bg-black/50 backdrop-blur-sm"
                        >
                            <div className="text-center">
                                <Loader2 className="w-12 h-12 animate-spin text-purple-400 mx-auto mb-4" />
                                <p className="text-white font-medium">
                                    {isLoadingSession ? 'Initializing Avatar...' : 'Processing...'}
                                </p>
                            </div>
                        </motion.div>
                    )}
                </AnimatePresence>

                {/* No Stream Placeholder */}
                {!stream && !isLoadingSession && (
                    <div className="absolute inset-0 flex items-center justify-center">
                        <div className="text-center">
                            <Video className="w-16 h-16 text-purple-400 mx-auto mb-4 opacity-50" />
                            <p className="text-white/70">Click "Start Session" to begin</p>
                        </div>
                    </div>
                )}

                {/* Controls Overlay */}
                {isSessionActive && (
                    <div className="absolute bottom-4 left-4 right-4 flex gap-2">
                        <Button
                            size="sm"
                            variant={isMuted ? 'destructive' : 'secondary'}
                            onClick={toggleMute}
                            className="flex-1"
                        >
                            {isMuted ? <VolumeX className="w-4 h-4 mr-2" /> : <Volume2 className="w-4 h-4 mr-2" />}
                            {isMuted ? 'Unmute' : 'Mute'}
                        </Button>
                        <Button
                            size="sm"
                            variant={isVideoEnabled ? 'secondary' : 'destructive'}
                            onClick={toggleVideo}
                            className="flex-1"
                        >
                            {isVideoEnabled ? <Video className="w-4 h-4 mr-2" /> : <VideoOff className="w-4 h-4 mr-2" />}
                            {isVideoEnabled ? 'Hide' : 'Show'}
                        </Button>
                    </div>
                )}
            </div>

            {/* Control Panel */}
            <div className="p-6 space-y-4">
                {/* Session Controls */}
                <div className="flex gap-2">
                    {!isSessionActive ? (
                        <Button
                            onClick={startSession}
                            disabled={isLoadingSession}
                            className="flex-1 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
                        >
                            {isLoadingSession ? (
                                <>
                                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                                    Connecting...
                                </>
                            ) : (
                                <>
                                    <Video className="w-4 h-4 mr-2" />
                                    Start Session
                                </>
                            )}
                        </Button>
                    ) : (
                        <Button
                            onClick={endSession}
                            variant="destructive"
                            className="flex-1"
                        >
                            <VideoOff className="w-4 h-4 mr-2" />
                            End Session
                        </Button>
                    )}
                </div>

                {/* Text Input */}
                {isSessionActive && (
                    <div className="space-y-2">
                        <div className="flex gap-2">
                            <input
                                type="text"
                                value={text}
                                onChange={(e) => setText(e.target.value)}
                                onKeyPress={(e) => e.key === 'Enter' && handleSpeak()}
                                placeholder="Type a message for the avatar to speak..."
                                className="flex-1 px-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white placeholder-white/50 focus:outline-none focus:ring-2 focus:ring-purple-500"
                                disabled={isLoadingRepeat}
                            />
                            <Button
                                onClick={handleSpeak}
                                disabled={isLoadingRepeat || !text.trim()}
                                className="bg-purple-600 hover:bg-purple-700"
                            >
                                {isLoadingRepeat ? (
                                    <Loader2 className="w-4 h-4 animate-spin" />
                                ) : (
                                    <Mic className="w-4 h-4" />
                                )}
                            </Button>
                        </div>

                        <Button
                            onClick={handleInterrupt}
                            variant="outline"
                            size="sm"
                            className="w-full"
                        >
                            <MicOff className="w-4 h-4 mr-2" />
                            Interrupt
                        </Button>
                    </div>
                )}

                {/* Debug Info */}
                {debug && (
                    <div className="text-xs text-white/60 font-mono bg-black/20 p-2 rounded">
                        {debug}
                    </div>
                )}
            </div>
        </Card>
    );
}

'use client';

import { useState, useRef, useEffect } from 'react';
import { Card } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Loader2, Video, Wand2, Download, Eye } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

export interface VideoGeneratorProps {
    className?: string;
}

export function HeyGenVideoGenerator({ className = '' }: VideoGeneratorProps) {
    const [script, setScript] = useState('');
    const [avatarId, setAvatarId] = useState('default');
    const [voiceId, setVoiceId] = useState('en-US-JennyNeural');
    const [isGenerating, setIsGenerating] = useState(false);
    const [videoId, setVideoId] = useState<string | null>(null);
    const [videoUrl, setVideoUrl] = useState<string | null>(null);
    const [progress, setProgress] = useState(0);
    const [error, setError] = useState<string | null>(null);

    // Ref for aborting fetch and polling
    const abortControllerRef = useRef<AbortController | null>(null);

    // Cleanup on unmount
    useEffect(() => {
        return () => {
            if (abortControllerRef.current) {
                abortControllerRef.current.abort();
            }
        };
    }, []);

    const handleGenerate = async () => {
        if (!script.trim()) {
            setError('Please enter a script');
            return;
        }

        // Cancel previous operation
        if (abortControllerRef.current) {
            abortControllerRef.current.abort();
        }

        const controller = new AbortController();
        abortControllerRef.current = controller;

        setIsGenerating(true);
        setError(null);
        setProgress(0);

        try {
            // Generate video
            const response = await fetch('/api/heygen/generate-video', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({
                    script,
                    avatarId,
                    voiceId,
                    title: 'EdIntel Educational Content',
                    aspectRatio: '16:9',
                }),
                signal: controller.signal
            });

            if (controller.signal.aborted) return;

            if (!response.ok) {
                throw new Error('Failed to generate video');
            }

            const data = await response.json();
            setVideoId(data.videoId);

            // Poll for completion with AbortSignal check
            let attempts = 0;
            const maxAttempts = 120; // 10 minutes (5s interval)

            while (attempts < maxAttempts) {
                if (controller.signal.aborted) break;

                attempts++;

                try {
                    const statusResponse = await fetch(
                        `/api/heygen/generate-video?videoId=${data.videoId}`,
                        { signal: controller.signal }
                    );
                    const status = await statusResponse.json();

                    if (controller.signal.aborted) return;

                    if (status.status === 'completed' && status.video_url) {
                        setVideoUrl(status.video_url);
                        setProgress(100);
                        setIsGenerating(false);
                        return;
                    } else if (status.status === 'failed') {
                        throw new Error('Video generation failed');
                    } else {
                        // Estimate progress
                        setProgress(prev => Math.min(prev + 5, 90));
                    }
                } catch (pollErr: any) {
                    if (pollErr.name === 'AbortError') return;
                    throw pollErr;
                }

                // Wait 5 seconds before next poll
                await new Promise((resolve, reject) => {
                    const timeout = setTimeout(resolve, 5000);
                    controller.signal.addEventListener('abort', () => {
                        clearTimeout(timeout);
                        reject(new DOMException('Aborted', 'AbortError'));
                    });
                });
            }

            if (attempts >= maxAttempts && !controller.signal.aborted) {
                throw new Error('Video generation timeout');
            }

        } catch (err: any) {
            if (err.name === 'AbortError') {
                console.log('Generation aborted');
                return;
            }
            setError(err instanceof Error ? err.message : 'Failed to generate video');
            setIsGenerating(false);
        } finally {
            if (abortControllerRef.current === controller) {
                abortControllerRef.current = null;
                // Only unset loading if we finished (success or error), not if we just started a new one (handled by new ref check?)
                // Actually if specific controller finishes, we turn off loading.
                // If a NEW controller started, ref wouldn't match, so we don't turn off loading (the new one keeps it on).
                if (!abortControllerRef.current) setIsGenerating(false);
                // Wait, if we cleared it above? No.
                // If this is the current controller, we are done.
            }
        }
    };

    return (
        <Card className={`p-6 bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 ${className}`}>
            <div className="space-y-6">
                {/* Header */}
                <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                        <div className="relative">
                            <div className="absolute inset-0 bg-purple-500/20 blur-xl rounded-full animate-pulse" />
                            <div className="p-4 bg-purple-500/10 border border-white/10 rounded-2xl relative">
                                <Video className="w-8 h-8 text-purple-400" />
                            </div>
                        </div>
                        <div>
                            <h3 className="text-2xl font-black text-white uppercase tracking-tighter">Avatar Forge</h3>
                            <p className="text-[10px] text-zinc-500 font-bold uppercase tracking-[0.2em] mt-1">Generative Neural Broadcasts</p>
