// Designed for AI Avatar Memory & Evidence Folder Architecture

generator client {
  provider = "prisma-client-js"
  output   = "../src/generated/client"
  // previewFeatures = ["postgresqlExtensions"]
}

datasource db {
  provider  = "postgresql"
